import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { API } from 'src/app/core/api/api-constants.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css']
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  selectedService: any = null;
  batches: any[] = [];

  loading = true;

  constructor(
    private http: HttpClient,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  loadServices(): void {
    this.http.get<any[]>(`${API.services}`).subscribe({
      next: res => {
        this.services = res;
        this.loading = false;
      },
      error: err => {
        console.error('Failed to load services', err);
        this.loading = false;
      }
    });
  }

  selectService(service: any): void {
    this.selectedService = service;
    this.loadBatches(service.serviceId);
  }

  loadBatches(serviceId: string): void {
    this.http.get<any[]>(`${API.batches}/service/${serviceId}`).subscribe({
      next: res => this.batches = res,
      error: err => console.error('Failed to load batches', err)
    });
  }

  bookBatch(batchId: string): void {
    this.router.navigate(['/user/book', batchId]);
  }
}
<div class="book-batch-container" *ngIf="batchDetails">

  <h2 class="title">Confirm Your Booking</h2>

  <!-- Batch Info -->
  <div class="batch-card">
    <p><strong>Batch:</strong> {{ batchDetails.batchName }}</p>
    <p><strong>Instructor:</strong> {{ batchDetails.instructorName }}</p>
    <p><strong>Start Date:</strong> {{ batchDetails.startDate | date }}</p>
    <p class="price">₹ {{ batchDetails.amount }}</p>
  </div>

  <!-- Mobile Input -->
  <div class="form-group">
    <label>Student Mobile Number *</label>
    <input
      type="tel"
      placeholder="Enter mobile number"
      [(ngModel)]="model.studentMobile"
      maxlength="15"
    />
  </div>

  <!-- Actions -->
  <div class="actions">
    <button
      class="btn-primary"
      (click)="confirmBooking()"
      [disabled]="bookingLoading"
    >
      {{ bookingLoading ? 'Booking...' : 'Confirm & Proceed to Payment' }}
    </button>

    <button class="btn-secondary" (click)="cancel()">Cancel</button>
  </div>
</div>

<!-- Loading -->
<p class="loading" *ngIf="!batchDetails">
  Loading batch details...
</p>
