import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  searchText: string = '';

  constructor(private router: Router) {}

  showNavbar(): boolean {
    return !this.router.url.includes('login')
        && !this.router.url.includes('select-role');
  }

  isLoggedIn(): boolean {
    return !!localStorage.getItem('token');
  }

  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  getUserName(): string {
    return localStorage.getItem('fullName') || 'Profile';
  }

  goToProfile() {
    this.router.navigate(['/profile']);
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }

  // üîç SEARCH LOGIC
  onSearch() {
    if (!this.searchText.trim()) {
      return;
    }

    this.router.navigate(
      ['/user/book'],
      { queryParams: { search: this.searchText.trim() } }
    );

    this.searchText = '';
  }
}
<!-- NAVBAR -->
<nav class="navbar navbar-expand-lg main-navbar" *ngIf="showNavbar()">
  <div class="container-fluid">

    <!-- Logo -->
    <a class="navbar-brand d-flex align-items-center gap-2" routerLink="/home">
      <img src="assets/Images/web-logo.png" alt="Beauty Logo" height="36" />
      <span class="navbar-brand brand-logo">Hairelite</span>
    </a>

    <div class="collapse navbar-collapse">

      <!-- LEFT NAV LINKS -->
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link" routerLink="/home">Home</a>
        </li>

        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/book">Book Batch</a>
        </li>

        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/bookings">My Bookings</a>
        </li>

        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/dashboard">Instructor Dashboard</a>
        </li>

        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/manage-batches">
           My Masterclasses
          </a>
        </li>
        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/view-bookings">
            Student Bookings
          </a>
        </li>
      </ul>

      <!-- üîç SEARCH BAR (CENTER) -->
      <div class="navbar-search" *ngIf="isUser()">
        <input
          type="text"
          class="search-input"
          placeholder="Search service or instructor..."
          [(ngModel)]="searchText"
          (keyup.enter)="onSearch()"
        />
        <button class="search-btn" (click)="onSearch()">
          Search
        </button>
      </div>

      <!-- RIGHT SIDE -->
      <div *ngIf="!isLoggedIn()">
        <button class="btn btn-primary-custom" routerLink="/login">
          Login
        </button>
      </div>

      <div class="profile-container" *ngIf="isLoggedIn()">
        <button class="btn profile-btn" (click)="goToProfile()">
          <i class="bi bi-person-circle profile-icon"></i>
          <span class="profile-name">{{ getUserName() }}</span>
        </button>

        <button class="btn logout-btn" (click)="logout()">
          <i class="bi bi-box-arrow-right"></i>
          Logout
        </button>
      </div>

    </div>
  </div>
</nav>

<router-outlet></router-outlet>
