import { Component } from '@angular/core';
import { ServiceService } from 'src/app/services/service.service';

@Component({
  selector: 'app-create-course',
  templateUrl: './create-course.component.html',
  styleUrls: ['./create-course.component.css']
})
export class CreateServiceComponent {

  name = '';
  description = '';
  price = 0;

  constructor(private serviceService: ServiceService) {}

  createService() {

    if (!this.name || !this.description || this.price <= 0) {
      alert('All fields are required');
      return;
    }

    const payload = {
      name: this.name,
      description: this.description,
      price: this.price
    };

    this.serviceService.createService(payload).subscribe({
      next: () => {
        alert('Course created successfully');
        this.name = '';
        this.description = '';
        this.price = 0;
      },
      error: () => {
        alert('Failed to create course');
      }
    });
  }
}
<div class="container mt-4">

  <h2 class="fw-bold mb-4">Create New Course</h2>

  <div class="card shadow-sm p-4">

    <!-- Course Name -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Course Name</label>
      <input
        type="text"
        class="form-control"
        [(ngModel)]="name"
        placeholder="Enter course name"
      />
    </div>

    <!-- Description -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Description</label>
      <textarea
        rows="4"
        class="form-control"
        [(ngModel)]="description"
        placeholder="Describe the course"
      ></textarea>
    </div>

    <!-- Price -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Price (₹)</label>
      <input
        type="number"
        class="form-control"
        [(ngModel)]="price"
        min="1"
      />
    </div>

    <!-- Submit -->
    <button class="btn btn-primary w-100" (click)="createService()">
      Create Course
    </button>

  </div>

</div>
