import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-select-role',
  templateUrl: './select-role.component.html',
  styleUrls: ['./select-role.component.css']
})
export class SelectRoleComponent {

  selectedRole: string | null = null;

  constructor(private auth: AuthService) {}

  selectRole(role: string) {
    this.selectedRole = role;
  }

  continue() {
    if (!this.selectedRole) return;

    this.auth.setRole(this.selectedRole);  // <-- call service method
  }
}
<div class="container text-center mt-5">

  <h2 class="fw-bold">Select Your Role</h2>
  <p class="text-muted mb-4">How do you want to use the Hairstylist App?</p>

  <div class="role-buttons">

    <button 
      class="btn btn-primary w-50 mb-3"
      [class.active]="selectedRole === 'User'"
      (click)="selectRole('User')">
      I am a User
    </button>

    <button 
      class="btn btn-secondary w-50"
      [class.active]="selectedRole === 'Instructor'"
      (click)="selectRole('Instructor')">
      I am an Instructor
    </button>

  </div>

  <button 
    class="btn btn-success w-50 mt-4"
    [disabled]="!selectedRole"
    (click)="continue()">
    Continue
  </button>

</div>
