import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { InstructorService } from '../../services/instructor.service';

@Component({
  selector: 'app-instructor-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class InstructorDashboardComponent implements OnInit {
  totalBatches = 0;
  totalStudents = 0;
  totalEarnings = 0;
  upcomingBatches = 0;

  loading = true;

  constructor(
    private router: Router,
    private instructorService: InstructorService
  ) {}

  ngOnInit(): void {
    this.loadDashboardSummary();
  }

  loadDashboardSummary(): void {
    this.instructorService.getDashboardSummary().subscribe({
      next: (res) => {
        console.log('Dashboard API response:', res);

        this.totalBatches = (res as any).TotalBatches;
        this.totalStudents = (res as any).TotalStudents;
        this.totalEarnings = (res as any).TotalEarnings;
        this.upcomingBatches = (res as any).UpcomingBatches;

        this.loading = false;
      },
      error: (err) => {
        console.error('Failed to load dashboard summary', err);
        this.loading = false;
      },
    });
  }

  goTo(page: string) {
    switch (page) {
      case 'create':
        this.router.navigate(['/instructor/create-batch']);
        break;

      case 'create-course':
        this.router.navigate(['/instructor/create-course']);
        break;

      case 'view-bookings':
        this.router.navigate(['/instructor/view-bookings']);
        break;
    }
  }
}
<div class="container py-4">
  <h2 class="page-title text-primary-custom mb-4">Instructor Dashboard</h2>

  <p class="text-muted mb-4">
    Manage your hairstyling batches, bookings, and view your student
    enrollments.
  </p>

  <div class="row g-4">
    <!-- Create New Batch -->
    <div class="col-md-4">
      <div
        class="card card-custom p-4 text-center hover-card"
        (click)="goTo('create')"
      >
        <i class="bi bi-plus-square-fill fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">Create New Batch</h5>
        <p class="text-muted">Add a new training batch for students.</p>
      </div>
    </div>

    <div class="col-md-4">
      <div
        class="card card-custom p-4 text-center hover-card"
        (click)="goTo('create-course')"
      >
        <i class="bi bi-plus-square-fill fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">Create New Course</h5>
        <p class="text-muted">Add a new training course for students.</p>
      </div>
    </div>

    <!-- ðŸ”¹ DASHBOARD SUMMARY -->
<div class="row g-4 mb-5">

  <!-- Total Batches -->
  <div class="col-md-3 col-sm-6">
    <div class="card card-custom stat-card text-center p-3">
      <i class="bi bi-layers-fill fs-2 mb-2"></i>
      <h4 class="fw-bold">{{ totalBatches }}</h4>
      <p class="text-muted mb-0">Total Batches</p>
    </div>
  </div>

  <!-- Total Students -->
  <div class="col-md-3 col-sm-6">
    <div class="card card-custom stat-card text-center p-3">
      <i class="bi bi-people-fill fs-2 mb-2"></i>
      <h4 class="fw-bold">{{ totalStudents }}</h4>
      <p class="text-muted mb-0">Students Enrolled</p>
    </div>
  </div>

  <!-- Earnings -->
  <div class="col-md-3 col-sm-6">
