      <i class="bi bi-calendar-event-fill fs-2 mb-2"></i>
      <h4 class="fw-bold">{{ upcomingBatches }}</h4>
      <p class="text-muted mb-0">Upcoming Batches</p>
    </div>
  </div>

</div>



