import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject, of } from 'rxjs';
import { tap } from 'rxjs/operators';
import { API } from 'src/app/core/api/api-constants.service';
import { Batch } from 'src/app/interfaces/Batch';
import { CreateBatchDto } from 'src/app/interfaces/dto/create-batch';
import { UpdateBatchDto } from 'src/app/interfaces/dto/update-batch';

@Injectable({
  providedIn: 'root',
})
export class BatchManageService {
  // üîπ Cache for instructor batches
  private batchesSubject = new BehaviorSubject<any[] | null>(null);

  constructor(private http: HttpClient) {}

  // üì¶ GET batches (used by dashboard ‚Üí cached)
  getInstructorBatches(): Observable<any[]> {
    if (this.batchesSubject.value) {
      return of(this.batchesSubject.value);
    }

    return this.http
      .get<any[]>(`${API.batchesManage}`)
      .pipe(tap((batches) => this.batchesSubject.next(batches)));
  }

  // üîÑ Force refresh
  refreshInstructorBatches(): Observable<any[]> {
    return this.http
      .get<any[]>(`${API.batchesManage}`)
      .pipe(tap((batches) => this.batchesSubject.next(batches)));
  }

  // ‚ûï Create batch
  // ‚ûï Create batch (FIXED)
  createBatch(payload: CreateBatchDto): Observable<any> {
    return this.http
      .post(API.batchesManage, payload)
      .pipe(tap(() => this.refreshInstructorBatches().subscribe()));
  }

  // üîç Get batch by id (EditBatchComponent)
  getBatchById(id: string): Observable<Batch> {
    return this.http.get<Batch>(`${API.batches}/${id}`);
  }

  // ‚úèÔ∏è Update batch
  updateBatch(batchId: string, payload: UpdateBatchDto): Observable<any> {
    return this.http
      .put(`${API.batchesManage}/${batchId}`, payload)
      .pipe(tap(() => this.refreshInstructorBatches().subscribe()));
  }

  // üóëÔ∏è Delete batch
  deleteBatch(batchId: string): Observable<any> {
    return this.http
      .delete(`${API.batchesManage}/${batchId}`)
      .pipe(tap(() => this.refreshInstructorBatches().subscribe()));
  }

  // üßπ Clear cache (on logout)
  clearCache(): void {
    this.batchesSubject.next(null);
  }
}
