import { Injectable, NgZone } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Injectable({ providedIn: 'root' })
export class AuthService {

  private googleClientId = '682018676352-6e452dp8cectm769t5t5roh3065h0n0h.apps.googleusercontent.com'; // <- replace
  private apiBase = 'https://localhost:7154/api/auth';

  constructor(
    private http: HttpClient,
    private router: Router,
    private zone: NgZone
  ) {}

  initGoogleLogin() {
    // @ts-ignore because window.google is from script
    google.accounts.id.initialize({
      client_id: this.googleClientId,
      callback: (response: any) => this.handleGoogleCredential(response)
    });

    // Render button into a div with id 'googleBtn'
    // @ts-ignore
    google.accounts.id.renderButton(
      document.getElementById('googleBtn'),
      { theme: 'outline', size: 'large', width: 260 }
    );
  }

  private handleGoogleCredential(response: any) {
    const idToken = response.credential;

    this.http.post(`${this.apiBase}/google-login`, { idToken })
      .subscribe({
        next: (res: any) => {
          // save your own JWT + user info
          localStorage.setItem('token', res.token);
          localStorage.setItem('userId', res.userId);
          localStorage.setItem('role', res.role);

          // run navigation inside NgZone
          this.zone.run(() => {
            if (res.role === 'Instructor') {
              this.router.navigate(['/instructor/dashboard']);
            } else {
              this.router.navigate(['/user/dashboard']);
            }
          });
        },
        error: err => {
          console.error('Google login failed:', err);
          alert('Google login failed. Try again.');
        }
      });
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
