import { Component, OnInit } from '@angular/core';
import { UserProfileService } from
  'src/app/services/users/user-profile.service';
import { UserProfile } from
  'src/app/interfaces/user-profile';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {

  profile!: UserProfile;
  loading = true;
  editMode = false;
  uploadingPhoto = false;

  constructor(private profileApi: UserProfileService) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  // ðŸ”¹ Load profile
  loadProfile(): void {
    this.profileApi.getProfile().subscribe({
      next: (res) => {
        this.profile = res;
        this.loading = false;
      },
      error: (err) => {
        console.error('Profile load failed', err);
        this.loading = false;
      }
    });
  }

  // ðŸ”¹ Save profile
  saveProfile(): void {
    this.profileApi.updateProfile(this.profile).subscribe({
      next: () => {
        this.editMode = false;
        alert('Profile updated successfully');
      },
      error: () => alert('Update failed')
    });
  }

  // ðŸ”¹ Upload profile photo
  onPhotoSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (!input.files || input.files.length === 0) return;

    const file = input.files[0];
    const formData = new FormData();
    formData.append('file', file);

    this.uploadingPhoto = true;

    this.profileApi.uploadPhoto(formData).subscribe({
      next: (res) => {
        this.profile.photoUrl = res.photoUrl;
        this.uploadingPhoto = false;
      },
      error: () => {
        alert('Photo upload failed');
        this.uploadingPhoto = false;
      }
    });
  }
}
<div class="profile-page">
  <h2 class="page-title">My Profile</h2>

  <!-- Loading -->
  <p *ngIf="loading" class="text-muted">Loading profile...</p>

  <!-- Profile Card -->
  <div *ngIf="!loading && profile" class="profile-card">
    <!-- HEADER -->
    <div class="profile-header">
      <!-- PHOTO -->
      <div class="photo-box">
        <img
          [src]="profile.photoUrl || 'assets/Images/default-avatar.png'"
          class="profile-avatar"
          alt="User Photo"
        />

        <label class="upload-btn">
          ðŸ“· Change Photo
          <input
            type="file"
            accept="image/png,image/jpeg,image/jpg,image/webp"
            (change)="onPhotoSelected($event)"
            hidden
          />
        </label>
      </div>

      <!-- BASIC INFO -->
      <div class="profile-basic">
        <h3>{{ profile.fullName }}</h3>
        <p class="email">{{ profile.email }}</p>
      </div>

      <!-- EDIT -->
      <button class="edit-btn" (click)="editMode = !editMode">
        {{ editMode ? "Cancel" : "Edit Profile" }}
      </button>
    </div>

    <!-- VIEW MODE -->
    <div class="profile-body" *ngIf="!editMode">
      <div class="info-row">
        <label>Full Name</label>
        <span>{{ profile.fullName }}</span>
      </div>

      <div class="info-row">
        <label>Email</label>
        <span>{{ profile.email }}</span>
      </div>

      <div class="info-row">
        <label>Mobile</label>
        <span>{{ profile.mobile || "Not added" }}</span>
      </div>
    </div>

    <!-- EDIT MODE -->
    <form *ngIf="editMode" class="profile-body" (ngSubmit)="saveProfile()">
      <div class="info-row">
        <label>Full Name</label>
        <input
          type="text"
          [(ngModel)]="profile.fullName"
          name="fullName"
          required
        />
      </div>

      <div class="info-row">
        <label>Mobile</label>
        <input
          type="tel"
          [(ngModel)]="profile.mobile"
          name="mobile"
          placeholder="Enter mobile number"
        />
      </div>

      <div class="actions">
        <button type="submit" class="save-btn">Save Changes</button>
      </div>
    </form>
  </div>
</div>
