import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BookingService } from 'src/app/services/booking.service';
import { PaymentService } from 'src/app/services/payment.service';
import { BookingDetails } from './booking-details';

declare var Razorpay: any;

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})
export class PaymentComponent implements OnInit {

  bookingId: string = '';
  amount: number = 0;

  booking: BookingDetails | null = null;
  paymentMethod: string = '';
  isProcessing = false; // ðŸ”’ safety flag

  constructor(
    private router: Router,
    private bookingApi: BookingService,
    private paymentApi: PaymentService
  ) {
    const nav = this.router.getCurrentNavigation();
    const state = nav?.extras.state as { bookingId: string; amount: number };

    if (state) {
      this.bookingId = state.bookingId;
      this.amount = state.amount;
    }
  }

  ngOnInit(): void {
    if (!this.bookingId) {
      alert('Invalid payment session');
      this.router.navigate(['/user/my-bookings']);
      return;
    }

    this.loadBookingDetails();
  }

  loadBookingDetails() {
    this.bookingApi.getBookingById(this.bookingId).subscribe({
      next: (res: BookingDetails) => {
        this.booking = res;
        this.amount = res.amount;
      },
      error: () => {
        alert('Failed to load booking details');
        this.router.navigate(['/user/my-bookings']);
      }
    });
  }

  selectMethod(method: string) {
    this.paymentMethod = method;
  }

  makePayment() {
    if (!this.paymentMethod || !this.booking || this.isProcessing) {
      return;
    }

    this.isProcessing = true;

    const orderPayload = {
      bookingId: this.bookingId,
      amount: this.amount
    };

    this.paymentApi.createOrder(orderPayload).subscribe({
      next: (order: any) => {

        const options: any = {
          key: order.key,
          amount: order.amount * 100,
          currency: 'INR',
          name: 'Hair Stylist Amar',
          description: 'Batch Booking Payment',
          order_id: order.orderId,

          method: {
            upi: true,
            card: true,
            netbanking: true,
            wallet: true
          },

          handler: (response: any) => {
            // âœ… CONFIRM PAYMENT ONLY HERE
            const verifyBody = {
              razorpayPaymentId: response.razorpay_payment_id,
              razorpayOrderId: response.razorpay_order_id,
              razorpaySignature: response.razorpay_signature,
              bookingId: this.bookingId
            };

            this.paymentApi.verifyPayment(verifyBody).subscribe({
              next: () => {
                alert('Payment Successful!');
                this.router.navigate(['/user/my-bookings']);
              },
              error: () => {
                alert('Payment verification failed');
                this.isProcessing = false;
              }
            });
          },

          modal: {
            ondismiss: () => {
              // ðŸ‘ˆ User closed Razorpay (NO PAYMENT)
              this.isProcessing = false;
            }
          }
        };

        const rzp = new Razorpay(options);
        rzp.open();
      },
      error: () => {
        alert('Failed to initiate payment');
        this.isProcessing = false;
      }
    });
  }
}
<div class="container py-4">

  <h2 class="page-title text-primary-custom mb-4">Complete Your Payment</h2>

  <!-- Booking Summary -->
  <div class="card card-custom p-4 mb-4 shadow-sm">

    <h5 class="fw-bold mb-3">
      <i class="bi bi-receipt text-primary-custom"></i> Booking Summary
    </h5>

    <ng-container *ngIf="booking; else loadingState">

      <p><strong>Batch:</strong> {{ booking.batchName }}</p>
      <p><strong>Instructor:</strong> {{ booking.instructorName }}</p>
      <p><strong>Start Date:</strong> {{ booking.startDate | date }}</p>

      <hr />

      <h5 class="fw-bold">
        Payment Amount:
        <span class="text-primary-custom">â‚¹{{ booking.amount }}</span>
      </h5>

    </ng-container>

    <ng-template #loadingState>
      <p class="text-muted">Loading booking details...</p>
    </ng-template>

  </div>


  <!-- Payment Method Section -->
  <div class="card card-custom p-4 mb-4 shadow-sm">

    <h5 class="fw-bold mb-3">
      <i class="bi bi-wallet2 text-primary-custom"></i> Select Payment Method
    </h5>

    <div class="list-group">

      <label
        class="list-group-item d-flex align-items-center"
        (click)="selectMethod('UPI')"
      >
        <input
          type="radio"
          name="paymentMethod"
          class="form-check-input me-3"
          [checked]="paymentMethod === 'UPI'"
        />
        <b>UPI</b> â€” Google Pay / PhonePe / Paytm
      </label>

      <label
        class="list-group-item d-flex align-items-center"
        (click)="selectMethod('Card')"
      >
        <input
          type="radio"
          name="paymentMethod"
          class="form-check-input me-3"
          [checked]="paymentMethod === 'Card'"
        />
        <b>Card Payment</b> â€” Debit / Credit Card
      </label>

      <label
        class="list-group-item d-flex align-items-center"
        (click)="selectMethod('NetBanking')"
      >
        <input
          type="radio"
          name="paymentMethod"
          class="form-check-input me-3"
          [checked]="paymentMethod === 'NetBanking'"
        />
        <b>Net Banking</b>
      </label>

    </div>
  </div>

  <!-- Pay Now Button -->
  <button
    class="btn btn-primary-custom w-100 py-2 fs-5"
    [disabled]="!paymentMethod || !booking"
    (click)="makePayment()"
  >
    <i class="bi bi-currency-rupee"></i> Pay Now
  </button>

</div>
