import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private router: Router) {}

  showNavbar(): boolean {
    return !(
      this.router.url.includes('login') ||
      this.router.url.includes('select-role')
    );
  }

  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  getUserName(): string {
    return localStorage.getItem('fullName') || "Profile";
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
<!-- NAVBAR -->
<nav class="navbar navbar-expand-lg main-navbar" *ngIf="showNavbar()">
  <div class="container-fluid">

    <!-- Logo -->
    <a class="navbar-brand fw-bold text-white" routerLink="/">
      <i class="bi bi-scissors me-1"></i>
      Hairstylist App
    </a>

    <!-- Mobile Toggle -->
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent">
      <span class="navbar-toggler-icon"></span>
    </button>

    <!-- Navbar Content -->
    <div class="collapse navbar-collapse" id="navbarSupportedContent">

      <!-- LEFT MENU -->
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">

        <!-- USER MENU ITEMS -->
        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/dashboard">Home</a>
        </li>

        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/book">Book Batch</a>
        </li>

        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/bookings">My Bookings</a>
        </li>

        <!-- INSTRUCTOR MENU ITEMS -->
        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/dashboard">Dashboard</a>
        </li>

        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/manage-batches">Manage Batches</a>
        </li>

        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/view-bookings">Bookings</a>
        </li>

      </ul>

      <!-- RIGHT PROFILE DROPDOWN -->
      <div class="dropdown" *ngIf="showNavbar()">
        <button class="btn btn-light dropdown-toggle d-flex align-items-center"
                type="button" data-bs-toggle="dropdown">

          <i class="bi bi-person-circle fs-4 me-2"></i>
          {{ getUserName() }}

        </button>

        <ul class="dropdown-menu dropdown-menu-end">
          <li><a class="dropdown-item" routerLink="/profile">My Profile</a></li>
          <li><hr class="dropdown-divider"></li>
          <li><button class="dropdown-item text-danger" (click)="logout()">Logout</button></li>
        </ul>
      </div>

    </div>
  </div>
</nav>

<!-- MAIN CONTENT -->
<div class="content-wrapper">

  <router-outlet></router-outlet>
</div>
