import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {

  role: 'User' | 'Instructor' | null = null;

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.role = localStorage.getItem('role') as any;
  }

  onLogin() {
    this.router.navigate(['/auth/login']);
  }

  goToDashboard() {
    if (this.role === 'User') {
      this.router.navigate(['/user/dashboard']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/dashboard']);
    }
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/']);
  }
}
<header class="navbar">
  <div class="nav-left">
    <img src="assets/Images/Site-Logo.png" alt="Hairvona Logo" class="logo" />

    <a class="nav-link active" routerLink="/">Home</a>

    <!-- DASHBOARD (User / Instructor) -->
    <a
      *ngIf="role"
      class="nav-link"
      (click)="goToDashboard()"
    >
      Dashboard
    </a>
  </div>

  <div class="nav-right">
    <input
      type="text"
      placeholder="Search service or instructor..."
      class="search-input"
    />

    <button class="btn btn-search">Search</button>

    <!-- LOGIN -->
    <button
      *ngIf="!role"
      class="btn btn-login"
      (click)="onLogin()"
    >
      Login
    </button>

    <!-- LOGOUT -->
    <button
      *ngIf="role"
      class="btn btn-login"
      (click)="logout()"
    >
      Logout
    </button>
  </div>
</header>
