// src/app/services/services/service-query.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { API, API_BASE } from 'src/app/core/api/api-constants.service';
import { ServiceSummary } from 'src/app/interfaces/service-summary';

@Injectable({
  providedIn: 'root'
})
export class ServiceQueryService {
  API_BASE =  API_BASE;

  constructor(private http: HttpClient) {}

  /**
   * Fetch all available services
   */
  getAllServices(): Observable<ServiceSummary[]> {
    return this.http.get<ServiceSummary[]>(`${API.services}`);
  }

    search(query: string): Observable<any> {
    return this.http.get<any>(`${this.API_BASE}?query=${query}`);
  }

}
