import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthApiService } from '../../../core/auth/auth-api.service';
import { AuthStorageService } from '../../../core/auth/auth-storage.service';
import { AuthNavigationService } from '../../../core/auth/auth-navigation.service';

@Component({
  selector: 'app-select-role',
  templateUrl: './select-role.component.html',
  styleUrls: ['./select-role.component.css']
})
export class SelectRoleComponent {

  selectedRole: 'User' | 'Instructor' | null = null;
  mobileNumber = '';
  isSubmitting = false;

  constructor(
    private authApi: AuthApiService,
    private authStorage: AuthStorageService,
    private authNavigation: AuthNavigationService,
    private router: Router
  ) {}

  selectRole(role: 'User' | 'Instructor'): void {

    const idToken = localStorage.getItem('google_id_token');

    if (!idToken) {
      alert('Google session expired. Please login again.');
      this.router.navigate(['/auth/login']);
      return;
    }

    if (role === 'Instructor' && !this.mobileNumber) {
      alert('Mobile number is required for Instructor.');
      return;
    }

    this.isSubmitting = true;

    this.authApi.googleLogin({
      idToken: idToken,
      role: role,
      mobileNumber: role === 'Instructor' ? this.mobileNumber : undefined
    }).subscribe({
      next: (res) => {

        // ✅ Clear temp token
        localStorage.removeItem('google_id_token');

        // ✅ Save login data
        this.authStorage.saveLogin(res);

        // ✅ Navigate by role
        this.authNavigation.navigateByRole(res.role);
      },
      error: (err) => {
        console.error('ROLE SET ERROR:', err);
        alert(err?.error || 'Failed to set role. Please try again.');
        this.isSubmitting = false;
      }
    });
  }
}
<div class="role-container">
  <div class="role-card">

    <h2 class="title">Choose Your Role</h2>
    <p class="subtitle">
      Please select how you want to continue
    </p>

    <!-- ROLE BUTTONS -->
    <div class="role-buttons" *ngIf="!selectedRole">
      <button
        class="role-btn user"
        (click)="selectRole('User')">
        I am a Student
      </button>

      <button
        class="role-btn instructor"
        (click)="selectedRole = 'Instructor'">
        I am an Instructor
      </button>
    </div>

    <!-- MOBILE INPUT (ONLY FOR INSTRUCTOR) -->
    <div class="mobile-box" *ngIf="selectedRole === 'Instructor'">
      <label>Instructor Mobile Number</label>

      <input
        type="tel"
        class="form-control"
        placeholder="Enter 10-digit mobile number"
        [(ngModel)]="mobileNumber"
        maxlength="10"
        pattern="[0-9]{10}"
      />

      <button
        class="btn btn-primary"
        [disabled]="mobileNumber.length !== 10 || isSubmitting"
        (click)="selectRole('Instructor')">
        Continue
      </button>
    </div>

  </div>
</div>
