import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private router: Router) {}

  // Hide navbar on login & role selection pages
  showNavbar(): boolean {
    return !(
      this.router.url.includes('login') ||
      this.router.url.includes('select-role')
    );
  }

  // Role detection (later from localStorage or JWT)
  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  // Logout placeholder
  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
<!-- NAVBAR (Shown on All Pages Except Login & Select Role) -->
<nav class="navbar navbar-expand-lg navbar-dark bg-primary px-3" *ngIf="showNavbar()">
  <a class="navbar-brand fw-bold" routerLink="/">Hairstylist App</a>

  <button class="navbar-toggler" type="button" data-bs-toggle="collapse"
          data-bs-target="#navbarContent">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarContent">

    <ul class="navbar-nav me-auto mb-2 mb-lg-0">
      <li class="nav-item" *ngIf="isUser()">
        <a class="nav-link" routerLink="/user/dashboard">Dashboard</a>
      </li>

      <li class="nav-item" *ngIf="isUser()">
        <a class="nav-link" routerLink="/user/book">Book Batch</a>
      </li>

      <li class="nav-item" *ngIf="isUser()">
        <a class="nav-link" routerLink="/user/bookings">My Bookings</a>
      </li>

      <li class="nav-item" *ngIf="isInstructor()">
        <a class="nav-link" routerLink="/instructor/dashboard">Instructor Panel</a>
      </li>

      <li class="nav-item" *ngIf="isInstructor()">
        <a class="nav-link" routerLink="/instructor/manage-batches">Manage Batches</a>
      </li>
    </ul>

    <!-- Logout Button -->
    <button class="btn btn-light btn-sm" *ngIf="showNavbar()" (click)="logout()">
      Logout
    </button>

  </div>
</nav>


<!-- MAIN CONTENT AREA -->
<div class="container-fluid py-4">
  <router-outlet></router-outlet>
</div>
