import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService } from 'src/app/services/service.service';
import { BatchService } from 'src/app/services/batch.service';
import { BookingService } from 'src/app/services/booking.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css']
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  batches: any[] = [];

  selectedServiceId: string = '';
  isLoading: boolean = false;

  constructor(
    private serviceApi: ServiceService,
    private batchApi: BatchService,
    private bookingApi: BookingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  // âœ… Load all services
  loadServices(): void {
    this.serviceApi.getAllServices().subscribe({
      next: (res) => {
        this.services = res;
      },
      error: (err) => {
        console.error("Error loading services:", err);
      }
    });
  }

  // âœ… Load batches based on selected service
  loadBatches(): void {
    if (!this.selectedServiceId) {
      this.batches = [];
      return;
    }

    this.isLoading = true;

    this.batchApi.getAllBatches(this.selectedServiceId).subscribe({
      next: (res) => {
        this.batches = res;
        this.isLoading = false;
      },
      error: (err) => {
        console.error("Error loading batches:", err);
        this.isLoading = false;
      }
    });
  }

  // âœ… Book a batch
  bookBatch(batchId: string): void {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      alert("Please login again.");
      this.router.navigate(['/login']);
      return;
    }

    // ðŸ‘‰ Correct booking payload
    const bookingData = {
      userId: userId,
      batchId: batchId,
      bookingDate: new Date() // backend overrides anyway
    };

    console.log("Sending booking request:", bookingData);

    // ðŸ‘‰ Correct API call
    this.bookingApi.createBooking(bookingData).subscribe({
      next: (res: any) => {
        alert("Batch booked successfully!");

        // Optional logs
        console.log("Booking created:", res);

        // Redirect to Payment
        this.router.navigate(['/user/payment'], {
          state: {
            bookingId: res.bookingId,
            amount: res.amount // if API returns it
          }
        });
      },
      error: (err) => {
        console.error("Error booking batch:", err);
        alert("Failed to book the batch. Please try again.");
      }
    });
  }

}
<div class="beauty-container py-4">

  <h2 class="page-title beauty-title mb-4">My Bookings</h2>

  <!-- No bookings message -->
  <div *ngIf="bookings.length === 0" class="text-center text-muted mt-5">
    <i class="bi bi-info-circle fs-2 text-primary-custom"></i>
    <p class="mt-2">You have not booked any batches yet.</p>

    <button class="beauty-btn mt-3" routerLink="/user/book">Book a Batch</button>
  </div>

  <!-- Bookings list -->
  <div class="row g-4" *ngIf="bookings.length > 0">

    <div class="col-md-4" *ngFor="let booking of bookings">

      <div class="beauty-card p-4 hover-beauty-card">

        <h5 class="fw-bold beauty-card-title">
          {{ booking.batch?.batchName }}
        </h5>

        <p class="beauty-info mb-1">
          <i class="bi bi-person-badge"></i>
          Instructor: {{ booking.batch?.instructor?.name }}
        </p>

        <p class="beauty-info mb-1">
          <i class="bi bi-calendar-event"></i>
          Start Date: {{ booking.batch?.startDate | date }}
        </p>

        <p class="beauty-info mb-1">
          <i class="bi bi-clock-history"></i>
          Booked on: {{ booking.bookingDate | date }}
        </p>

        <!-- Status Badge -->
        <span class="beauty-badge mt-3"
              [ngClass]="{
                'badge-confirmed': booking.status === 'Confirmed',
                'badge-pending': booking.status === 'Pending',
                'badge-cancelled': booking.status === 'Cancelled'
              }">
          {{ booking.status }}
        </span>

        <!-- Payment Button -->
        <button *ngIf="booking.status === 'Pending'"
                class="beauty-btn w-100 mt-3"
                (click)="makePayment(booking)">
          Pay Now
        </button>

      </div>

    </div>

  </div>

</div>
