import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { API } from 'src/app/core/api/api-constants.service';

export interface InstructorProfile {
  instructorId: string;
  fullName: string;
  email: string;
  mobile: string;
  name: string;
  specialization: string;
  photoUrl: string;
  description: string;
  isActive: boolean;
}

@Injectable({
  providedIn: 'root'
})
export class InstructorProfileService {

  constructor(private http: HttpClient) {}

  // ✅ GET LOGGED-IN INSTRUCTOR PROFILE
  getMyProfile(): Observable<InstructorProfile> {
    return this.http.get<InstructorProfile>(
      `${API.instructorProfile}/me`
    );
  }


  // ✅ UPDATE LOGGED-IN INSTRUCTOR PROFILE
  updateMyProfile(profile: InstructorProfile): Observable<void> {
    return this.http.put<void>(
      `${API.instructorProfile}/me`,
      profile
    );
  }


}
