import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject, of } from 'rxjs';
import { tap } from 'rxjs/operators';
import { API, API_BASE } from 'src/app/core/api/api-constants.service';
import { InstructorProfile } from 'src/app/interfaces/instructor-profile';
import { UpdateInstructorProfileDto } from
  'src/app/interfaces/update-instructor-profile-dto';

@Injectable({
  providedIn: 'root'
})
export class InstructorProfileService {

  API: string = API_BASE;

  // üîπ Cache holder
  private profileSubject = new BehaviorSubject<InstructorProfile | null>(null);

  constructor(private http: HttpClient) {}

  // ‚úÖ GET profile (CACHED)
  getMyProfile(): Observable<InstructorProfile> {
    if (this.profileSubject.value) {
      return of(this.profileSubject.value);
    }

    return this.http.get<InstructorProfile>(
      `${API.instructorProfile}/me`
    ).pipe(
      tap(profile => this.profileSubject.next(profile))
    );
  }

  // üîÑ FORCE refresh (after update / upload)
  refreshProfile(): Observable<InstructorProfile> {
    return this.http.get<InstructorProfile>(
      `${API.instructorProfile}/me`
    ).pipe(
      tap(profile => this.profileSubject.next(profile))
    );
  }

  // ‚úèÔ∏è UPDATE profile
  updateMyProfile(profile: UpdateInstructorProfileDto): Observable<void> {
    return this.http.put<void>(
      `${this.API}/instructor/profile/me`,
      profile
    ).pipe(
      tap(() => {
        // ‚úÖ refresh cache after update
        this.refreshProfile().subscribe();
      })
    );
  }

  // üì∏ UPLOAD photo
  uploadPhoto(formData: FormData): Observable<{ photoUrl: string }> {
    return this.http.post<{ photoUrl: string }>(
      `${API.instructorProfile}/upload-photo`,
      formData
    ).pipe(
      tap(() => {
        // ‚úÖ refresh cache after photo upload
        this.refreshProfile().subscribe();
      })
    );
  }

  // üßπ CLEAR cache (call on logout)
  clearCache(): void {
    this.profileSubject.next(null);
  }
}

export { InstructorProfile };
