import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService } from 'src/app/services/service.service';
import { BatchService } from 'src/app/services/batch.service';
import { BookingService } from 'src/app/services/booking.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css']
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  batches: any[] = [];

  selectedServiceId: string = '';
  isLoading = false;

  constructor(
    private serviceApi: ServiceService,
    private batchApi: BatchService,
    private bookingApi: BookingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  // Load all services
  loadServices() {
    this.serviceApi.getAllServices().subscribe({
      next: (res: any) => {
        this.services = res;
      },
      error: (err: any) => {
        console.error("Error loading services:", err);
      }
    });
  }

  // Load batches for selected service
  loadBatches() {
    if (!this.selectedServiceId) {
      this.batches = [];
      return;
    }

    this.isLoading = true;

    this.batchApi.getAllBatches(this.selectedServiceId).subscribe({
      next: (res: any) => {
        this.batches = res;
        this.isLoading = false;
      },
      error: (err) => {
        console.error("Error loading batches:", err);
        this.isLoading = false;
      }
    });
  }

  // Book a batch
  bookBatch(batchId: string) {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      alert("Please login again.");
      this.router.navigate(['/login']);
      return;
    }

    const bookingData = {
      userId: userId,
      batchId: batchId,
      bookingDate: new Date()
    };

    this.bookingApi.createBooking(bookingData).subscribe({
      next: (res: any) => {
        alert("Batch booked successfully!");

        this.router.navigate(['/user/payment'], {
          state: {
            bookingId: res.bookingId,
            amount: res.amount
          }
        });
      },
      error: (err) => console.error("Error booking batch:", err)
    });
  }

}
<div class="container py-4">

  <h2 class="page-title text-primary-custom">Book a Batch</h2>

  <!-- Select Service Section -->
  <div class="card card-custom mb-4 p-3">
    <h5 class="fw-bold mb-3">Choose a Service</h5>

    <select class="form-select"
            [(ngModel)]="selectedServiceId"
            (change)="loadBatches()">

      <option value="">-- Select a service --</option>

      <option *ngFor="let service of services"
              [value]="service.serviceId">
        {{ service.name }} — ₹{{ service.price }}
      </option>

    </select>
  </div>

  <!-- Loader -->
  <div *ngIf="isLoading" class="text-center mt-4">
    <div class="spinner-border text-primary"></div>
    <p class="text-muted mt-2">Loading batches...</p>
  </div>

  <!-- Batches Listing -->
  <div *ngIf="batches && batches.length > 0 && !isLoading">
    <h4 class="fw-bold mb-3">Available Batches</h4>

    <div class="row g-4">
      <div class="col-md-4" *ngFor="let batch of batches">
        <div class="card card-custom p-3 hover-card">

          <h5 class="fw-bold">{{ batch.batchName }}</h5>

          <p class="text-muted mb-1">
            <i class="bi bi-person-badge"></i>
            Instructor:
            <strong>{{ batch.instructor?.name ?? "Not Assigned" }}</strong>
          </p>

          <p class="text-muted mb-1">
            <i class="bi bi-calendar-event"></i>
            Start Date: {{ batch.startDate | date }}
          </p>

          <p class="text-muted mb-1">
            <i class="bi bi-cash-stack"></i>
            Price: <strong>₹{{ batch.price }}</strong>
          </p>

          <button class="btn btn-primary-custom w-100 mt-2"
                  (click)="bookBatch(batch.batchId)">
            Book Now
          </button>

        </div>
      </div>
    </div>
  </div>

  <!-- No batches message -->
  <div *ngIf="selectedServiceId && batches.length === 0 && !isLoading"
       class="mt-4 text-center text-muted">
    <i>No batches available for this service.</i>
  </div>

</div>
