import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  constructor(
    private auth: AuthService,
    private router: Router
  ) {}


  ngOnInit(): void {
    this.auth.initGoogleLogin();
  }

  loginWithGoogle() {
    console.log("Google Login Clicked Manually");

    this.router.navigate(['/select-role']);
  }
}
<div class="container-fluid login-bg d-flex justify-content-center align-items-center min-vh-100">

  <div class="card login-card shadow-lg p-4 gradient-card">

    <!-- App Title -->
    <h3 class="text-center mb-2 fw-bold text-primary-custom">Hairstylist App</h3>
    <p class="text-center text-muted mb-4">Sign in to continue</p>

    <!-- Logo -->
    <div class="text-center mb-3">
      <img src="assets/logo.png" class="logo-img soft-logo" alt="App Logo" />
    </div>

    <!-- Auto-rendered Google Button -->
    <div id="googleBtn" class="d-flex justify-content-center mb-3"></div>

    <!-- Terms -->
    <p class="text-center mt-4 small text-muted">
      By continuing, you agree to our
      <a href="#" class="link-custom">Terms</a> & 
      <a href="#" class="link-custom">Privacy Policy</a>
    </p>

  </div>

</div>
