import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
selector: 'app-instructor-dashboard',
 templateUrl: './dashboard.component.html',
 styleUrls: ['./dashboard.component.css']

})
export class InstructorDashboardComponent {

  constructor(private router: Router) {}

  goTo(page: string) {
    switch (page) {
      case 'create':
        this.router.navigate(['/instructor/create-batch']);
        break;

      case 'create-course':
        this.router.navigate(['/instructor/create-course']);
        break;

      case 'my-bookings':
        this.router.navigate(['/instructor/view-bookings']);
        break;
    }
  }

}
<div class="container py-4">

  <h2 class="page-title text-primary-custom mb-4">Instructor Dashboard</h2>

  <p class="text-muted mb-4">
    Manage your hairstyling batches, bookings, and view your student enrollments.
  </p>

  <div class="row g-4">

    <!-- Create New Batch -->
    <div class="col-md-4">
      <div class="card card-custom p-4 text-center hover-card" (click)="goTo('create')">
        <i class="bi bi-plus-square-fill fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">Create New Batch</h5>
        <p class="text-muted">Add a new training batch for students.</p>
      </div>
    </div>

     <div class="col-md-4">
      <div class="card card-custom p-4 text-center hover-card" (click)="goTo('create-course')">
        <i class="bi bi-plus-square-fill fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">Create New Course</h5>
        <p class="text-muted">Add a new training course for students.</p>
      </div>
    </div>


  </div>

</div>
