import { Component, HostListener } from '@angular/core';
import { Router } from '@angular/router';
import {
  AuthStorageService,
  UserRole
} from '../../core/auth/auth-storage.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  role: UserRole = null;
  menuOpen = false;
  searchText = '';
  hideSearch = false;
  showLogoutPopup = false;

  private lastScrollTop = 0;

  constructor(
    private router: Router,
    private authStorage: AuthStorageService
  ) {
    // ðŸ” React to login / logout / role change
    this.authStorage.role$.subscribe(role => {
      this.role = role;
    });
  }

  // ðŸ”½ Hide / show search on scroll
  @HostListener('window:scroll', [])
  onScroll(): void {
    const scrollTop =
      window.pageYOffset || document.documentElement.scrollTop;

    if (scrollTop > this.lastScrollTop && scrollTop > 80) {
      this.hideSearch = true;
    } else {
      this.hideSearch = false;
    }

    this.lastScrollTop = scrollTop <= 0 ? 0 : scrollTop;
  }

  // ðŸ” LOGIN
  onLogin(): void {
    this.router.navigate(['/auth/login']);
  }

  // ðŸ“Š DASHBOARD
  goToDashboard(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/dashboard']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/dashboard']);
    }
  }

  // ðŸ—‚ï¸ MANAGE BATCHES
  goToManageBatches(): void {
    if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/batches']);
    }
  }

  // ðŸ“– VIEW BOOKINGS
  goToViewBookings(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/bookings']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/bookings']);
    }
  }

  // ðŸ“š BOOK BATCH
  goToBookBatch(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/book-batch']);
    }
  }

  // ðŸ” SEARCH
  onSearch(): void {
    if (!this.searchText || !this.searchText.trim()) {
      return;
    }

    this.router.navigate(['/search-results'], {
      queryParams: { q: this.searchText.trim() }
    });

    this.searchText = '';
  }

  // ðŸ‘¤ PROFILE
  goToProfile(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/profile']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/profile']);
    }
  }

  // â“ LOGOUT CONFIRMATION (NEW)
  confirmLogout(): void {
    const confirmLogout = window.confirm('Do you want to logout?');

    if (confirmLogout) {
      this.logout();
    }
  }

  // ðŸšª LOGOUT (UNCHANGED)
  logout(): void {
    this.authStorage.clearAll();
    this.router.navigate(['/']);
  }
}
<header class="navbar">
  <div class="nav-left">
    <img
      src="assets/Images/Site-Logo.png"
      alt="Hairvona Logo"
      class="logo"
      routerLink="/"
    />

    <a class="nav-link desktop-only" routerLink="/">Home</a>

    <a *ngIf="role" class="nav-link desktop-only" (click)="goToDashboard()">
      Dashboard
    </a>

    <a
      *ngIf="role === 'User'"
      class="nav-link desktop-only"
      (click)="goToBookBatch()"
    >
      Book Batch
    </a>

    <a
      *ngIf="role === 'Instructor'"
      class="nav-link desktop-only"
      (click)="goToManageBatches()"
    >
      Manage Batches
    </a>

    <a *ngIf="role" class="nav-link desktop-only" (click)="goToViewBookings()">
      View Bookings
    </a>
  </div>

  <!-- ðŸ” SEARCH -->
  <div class="search-wrapper" [class.hide]="hideSearch">
    <input
      type="text"
      placeholder="Search service or instructor..."
      class="search-input"
      [(ngModel)]="searchText"
      (keyup.enter)="onSearch()"
    />
    <button class="btn btn-search" (click)="onSearch()">Search</button>
  </div>

  <!-- DESKTOP RIGHT -->
  <div class="nav-right desktop-only">
    <a *ngIf="role" class="nav-link" (click)="goToProfile()">Profile</a>

    <button *ngIf="!role" class="btn btn-login" (click)="onLogin()">
      Login
    </button>

    <!-- âœ… UPDATED -->
    <button *ngIf="role" class="btn btn-login" (click)="confirmLogout()">
      Logout
    </button>
  </div>

  <!-- â˜° Hamburger -->
  <div class="hamburger mobile-only" (click)="menuOpen = true">â˜°</div>
</header>

<!-- ðŸ“± Mobile Menu -->
<div class="mobile-menu" [class.open]="menuOpen">
  <div class="menu-header">
    <span class="menu-title">Menu</span>
    <button class="menu-close" (click)="menuOpen = false">âœ•</button>
  </div>

  <a routerLink="/" (click)="menuOpen = false">Home</a>

  <a *ngIf="role" (click)="goToDashboard(); menuOpen = false">
    Dashboard
  </a>

  <a *ngIf="role === 'User'" (click)="goToBookBatch(); menuOpen = false">
    Book Batch
  </a>

  <a
    *ngIf="role === 'Instructor'"
    (click)="goToManageBatches(); menuOpen = false"
  >
    Manage Batches
  </a>

  <a *ngIf="role" (click)="goToViewBookings(); menuOpen = false">
    View Bookings
  </a>

  <a *ngIf="role" (click)="goToProfile(); menuOpen = false">Profile</a>
  <a *ngIf="!role" (click)="onLogin(); menuOpen = false">Login</a>

  <!-- âœ… UPDATED -->
  <a *ngIf="role" (click)="confirmLogout(); menuOpen = false">
    Logout
  </a>
</div>


<!-- ðŸ”” Logout Confirmation Modal -->
<div class="logout-overlay" *ngIf="showLogoutPopup">
  <div class="logout-modal">
    <h3>Logout</h3>
    <p>Do you want to logout?</p>

    <div class="logout-actions">
      <button class="btn-cancel" (click)="showLogoutPopup = false">
        Cancel
      </button>
      <button class="btn-logout" (click)="logout()">
        Logout
      </button>
    </div>
  </div>
</div>
