import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { API } from './endpoints.service';
import { BookingDetails } from '../user/payment/booking-details';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class BookingService {

  constructor(private http: HttpClient) {}

  // ----------------------------------------
  // CREATE BOOKING (initial = Pending)
  // ----------------------------------------
  createBooking(data: any): Observable<any> {
    return this.http.post<any>(`${API.bookings}`, data);
  }

  // ----------------------------------------
  // GET BOOKINGS BY USER  ✅ FIXED TYPING
  // ----------------------------------------
  getBookingsByUser(userId: string): Observable<any[]> {
    return this.http.get<any[]>(`${API.bookings}/user/${userId}`);
  }

  // ----------------------------------------
  // GET BOOKINGS BY INSTRUCTOR
  // ----------------------------------------
  getBookingsByInstructor(instructorId: string): Observable<any[]> {
    return this.http.get<any[]>(`${API.bookings}/instructor/${instructorId}`);
  }

  // ----------------------------------------
  // GET BOOKING DETAILS (PAYMENT PAGE)
  // ----------------------------------------
  getBookingById(id: string): Observable<BookingDetails> {
    return this.http.get<BookingDetails>(`${API.bookings}/details/${id}`);
  }

  // ----------------------------------------
  // CANCEL BOOKING (Pending only)
  // ----------------------------------------
  cancelBooking(bookingId: string): Observable<any> {
    return this.http.delete<any>(`${API.bookings}/cancel/${bookingId}`);
  }
}
