import { Component, OnInit } from '@angular/core';
import { UserProfileService } from
  'src/app/services/users/user-profile.service';
import { UserProfile } from
  'src/app/interfaces/user-profile';

@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent implements OnInit {

  profile!: UserProfile;
  loading = true;
  editMode = false;

  constructor(private profileApi: UserProfileService) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  // ðŸ”¹ Load profile
  loadProfile(): void {
    this.profileApi.getProfile().subscribe({
      next: (res) => {
        this.profile = res;
        this.loading = false;
      },
      error: (err) => {
        console.error('Profile load failed', err);
        this.loading = false;
      }
    });
  }

  // ðŸ”¹ Save profile
  saveProfile(): void {
    this.profileApi.updateProfile(this.profile).subscribe({
      next: () => {
        this.editMode = false;
        alert('Profile updated successfully');
      },
      error: () => alert('Update failed')
    });
  }
}
