import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BatchManageService } from 'src/app/services/batches/batch-manage.service';
import { BatchQueryService } from 'src/app/services/batches/batch-query.service';


@Component({
  selector: 'app-edit-batch',
  templateUrl: '../create-batch/create-batch.component.html', // ‚úÖ reuse HTML
  styleUrls: ['../create-batch/create-batch.component.css']   // ‚úÖ reuse CSS
})
export class EditBatchComponent implements OnInit {

  // ===============================
  // Form Model (same as create)
  // ===============================
  batchId = '';

  batchName = '';
  description = '';
  price = 0;
  totalSeats = 0;
  availableSeats = 0;
  startDate = '';
  serviceId = '';
  mobileNumber = '';

  minStartDate = '';

  services: any[] = [];
  isSubmitting = false;

  constructor(
    private route: ActivatedRoute,
    private batchManageService: BatchManageService,
    private serviceQueryService: BatchQueryService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.batchId = this.route.snapshot.paramMap.get('batchId')!;
    this.setMinStartDate();
    this.fetchServices();
    this.loadBatchDetails();
  }

  // ===============================
  // Load batch data
  // ===============================
  loadBatchDetails(): void {
    this.batchManageService.getBatchById(this.batchId).subscribe({
      next: (batch) => {
        this.batchName = batch.batchName;
        this.description = batch.description;
        this.price = batch.price;
        this.totalSeats = batch.totalSeats;
        this.availableSeats = batch.availableSeats;
        this.startDate = batch.startDate;
        this.serviceId = batch.serviceId;
        this.mobileNumber = batch.mobileNumber;
      },
      error: (err: any) => {
        console.error('Failed to load batch', err);
      }
    });
  }

  // ===============================
  // Submit (UPDATE)
  // ===============================
  submitBatch(): void {

    if (this.isSubmitting) return;

    const payload = {
      batchName: this.batchName,
      description: this.description,
      price: this.price,
      totalSeats: this.totalSeats,

      // ‚ùó preserve available seats
      availableSeats: this.availableSeats,

      startDate: this.startDate,
      serviceId: this.serviceId,
      mobileNumber: this.mobileNumber
    };

    this.isSubmitting = true;

    this.batchManageService.updateBatch(this.batchId, payload).subscribe({
      next: () => {
        this.router.navigate(['/instructor/batches']);
      },
      error: (err) => {
        console.error('Update failed', err);
        this.isSubmitting = false;
      }
    });
  }

  // ===============================
  // Helpers
  // ===============================
  setMinStartDate(): void {
    const today = new Date();
    this.minStartDate = today.toISOString().split('T')[0];
  }

  fetchServices(): void {
    this.serviceQueryService.getAllServices().subscribe({
      next: (services) => (this.services = services),
      error: (err) => console.error(err)
    });
  }
}
<div class="batch-container">
  <h2 class="page-title">Create New Batch</h2>

  <form class="batch-form" (ngSubmit)="submitBatch()" #batchForm="ngForm">
    <!-- Batch Name -->
    <div class="form-group">
      <label>Batch Name</label>
      <input
        type="text"
        class="form-control"
        name="batchName"
        [(ngModel)]="batchName"
        required
        placeholder="Enter batch name"
      />
    </div>

    <!-- Description -->
    <div class="form-group">
      <label>Description</label>
      <textarea
        class="form-control"
        rows="3"
        name="description"
        [(ngModel)]="description"
        required
        placeholder="Brief description of the batch"
      ></textarea>
    </div>

    <!-- Price -->
    <!-- Price & Seats (2-column on desktop) -->
<div class="form-row">

  <div class="form-group">
    <label>Price (‚Çπ)</label>
    <div class="input-icon">
      <span class="icon">‚Çπ</span>
      <input
        type="number"
        class="form-control"
        name="price"
        [(ngModel)]="price"
        required
        min="0"
      />
    </div>
  </div>

  <div class="form-group">
    <label>Total Seats</label>
    <div class="input-icon">
      <span class="icon">üéüÔ∏è</span>
      <input
        type="number"
        class="form-control"
        name="totalSeats"
        [(ngModel)]="totalSeats"
        required
        min="1"
      />
    </div>
  </div>

</div>


    <!-- Start Date -->
    <div class="form-group">
  <label>Start Date</label>
  <div class="input-icon">
    <span class="icon">üìÖ</span>
    <input
      type="date"
      class="form-control"
      name="startDate"
      [(ngModel)]="startDate"
      [min]="minStartDate"
      required
    />
  </div>
</div>


    <!-- Service -->
    <div class="form-group">
      <label>Service</label>
      <select
        class="form-control"
        name="serviceId"
        [(ngModel)]="serviceId"
        required
      >
        <option value="">-- Select Service --</option>
        <option *ngFor="let service of services" [value]="service.serviceId">
          {{ service.name }}
        </option>
      </select>
    </div>

    <!-- Contact Mobile Number -->
   <div class="form-group">
  <label>Contact Mobile Number</label>
  <div class="input-icon">
    <span class="icon">üìû</span>
    <input
      type="tel"
      class="form-control"
      name="mobileNumber"
      [(ngModel)]="mobileNumber"
      required
      pattern="[0-9]{10}"
      placeholder="Enter 10-digit mobile number"
    />
  </div>
</div>


    <!-- Submit -->
    <button
      class="btn btn-primary-custom"
      type="submit"
      [disabled]="batchForm.invalid"
    >
      Create Batch
    </button>
  </form>
</div>
