import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BatchService } from '../../services/batch.service';
import { ServiceService } from '../../services/service.service';

@Component({
  selector: 'app-create-batch',
  templateUrl: './create-batch.component.html',
  styleUrls: ['./create-batch.component.css'],
})
export class CreateBatchComponent implements OnInit {
  services: any[] = [];

  serviceId: string = '';
  batchName: string = '';
  description: string = '';
  price: number = 0;
  startDate: string = '';

  // ✅ NEW: minimum date (today)
  minDate: string = '';

  constructor(
    private serviceApi: ServiceService,
    private batchApi: BatchService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
    this.setMinDate();
  }

  // ✅ Set today date in yyyy-MM-dd
  setMinDate() {
    const today = new Date();
    this.minDate = today.toISOString().split('T')[0];
  }

  loadServices() {
    this.serviceApi.getAllServices().subscribe({
      next: (res) => (this.services = res),
      error: (err) => console.log(err),
    });
  }

  createBatch() {
    const instructorId = localStorage.getItem('instructorId');

    if (!instructorId) {
      alert('Please login again.');
      this.router.navigate(['/login']);
      return;
    }

    if (!this.serviceId) {
      alert('Please select a service!');
      return;
    }

    if (!this.startDate) {
      alert('Please select a valid start date!');
      return;
    }

    const newBatch = {
      batchId: '00000000-0000-0000-0000-000000000000',
      batchName: this.batchName,
      description: this.description,
      price: this.price,
      startDate: this.startDate,
      instructorId: instructorId,
      serviceId: this.serviceId,
    };

    this.batchApi.createBatch(newBatch).subscribe({
      next: () => {
        alert('Batch created successfully!');
        this.router.navigate(['/instructor/manage-batches']);
      },
      error: (err) => {
        console.error('Batch creation error:', err);
        alert('Failed to create batch: ' + JSON.stringify(err.error));
      },
    });
  }
}
<div class="container mt-4">

  <h2 class="fw-bold mb-4">Create New Batch</h2>

  <div class="card shadow-sm p-4">

    <!-- Service Selection -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Select Service</label>
      <select class="form-select" [(ngModel)]="serviceId">
        <option value="">-- Choose a Service --</option>
        <option *ngFor="let s of services" [value]="s.serviceId">
          {{ s.name }}
        </option>
      </select>
    </div>

    <!-- Batch Name -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Batch Name</label>
      <input
        type="text"
        class="form-control"
        [(ngModel)]="batchName"
        placeholder="Enter batch name"
      />
    </div>

    <!-- Description -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Description</label>
      <textarea
        rows="4"
        class="form-control"
        [(ngModel)]="description"
        placeholder="Describe this batch"
      ></textarea>
    </div>

    <!-- Price -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Price (₹)</label>
      <input
        type="number"
        class="form-control"
        [(ngModel)]="price"
        min="1"
      />
    </div>

    <!-- ✅ Start Date (ONLY FUTURE DATES) -->
    <div class="mb-3">
      <label class="form-label fw-semibold">Start Date</label>
      <input
        type="date"
        class="form-control"
        [(ngModel)]="startDate"
        [min]="minDate"
      />
    </div>

    <!-- Submit -->
    <button class="btn btn-primary w-100" (click)="createBatch()">
      Create Batch
    </button>

  </div>

</div>
