import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { UserService } from 'src/app/services/user.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css'],
})
export class ProfileComponent implements OnInit {
  profile: any = null;
  role: string | null = null;

  // ✅ NEW
  instructorMobile: string | null = null;

  constructor(
    private authService: AuthService,
    private userService: UserService,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit(): void {
    this.role = localStorage.getItem('role');
    const userId = localStorage.getItem('userId');

    if (!userId) {
      alert('User not logged in');
      return;
    }

    // --------------------
    // USER PROFILE
    // --------------------
    this.userService.getProfile().subscribe({
      next: (res) => (this.profile = res),
      error: () => alert('Failed to load profile'),
    });

    // --------------------
    // INSTRUCTOR MOBILE (ONLY IF INSTRUCTOR)
    // --------------------
    if (this.role === 'Instructor') {
      const instructorId = localStorage.getItem('instructorId');

      if (instructorId) {
        this.http
          .get<any>(`https://localhost:7154/api/instructors/${instructorId}`)
          .subscribe({
            next: (res) => {
              this.instructorMobile = res.contactMobile;
            },
            error: () => {
              console.warn('Failed to load instructor contact');
            }
          });
      }
    }
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/home']);
  }
}
<div class="profile-wrapper" *ngIf="profile">
  <div class="profile-card">
    <!-- Profile Icon -->
    <div class="profile-icon-wrapper">
      <i class="bi bi-person-circle profile-icon"></i>
    </div>

    <!-- Name -->
    <h2 class="profile-name">
      {{ profile.fullName }}
    </h2>

    <!-- Info -->
    <div class="profile-info">
      <!-- Email -->
      <div class="info-row">
        <i class="bi bi-envelope"></i>
        <span>{{ profile.email }}</span>
      </div>

      <!-- ✅ USER MOBILE (ALL ROLES) -->
      <div class="info-row" *ngIf="profile.mobile">
        <i class="bi bi-telephone"></i>
        <span>{{ profile.mobile }}</span>
      </div>

      <!-- ✅ INSTRUCTOR MOBILE (ONLY FOR INSTRUCTOR) -->
      <div class="info-row" *ngIf="role === 'Instructor' && instructorMobile">
        <i class="bi bi-telephone-fill"></i>
        <span>{{ instructorMobile }}</span>
      </div>
    </div>

    <!-- Action Buttons -->
    <div class="profile-actions">
      <button class="btn logout-btn" (click)="logout()">
        <i class="bi bi-box-arrow-right"></i>
        Logout
      </button>
    </div>
  </div>
</div>
