import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { InstructorDashboardService } from
  '../../../services/instructors/instructor-dashboard.service';
import { BatchManageService } from
  'src/app/services/batches/batch-manage.service';

@Component({
  selector: 'app-instructor-batches',
  templateUrl: './batches.component.html',
  styleUrls: ['./batches.component.css']
})
export class InstructorBatchesComponent implements OnInit {

  batches: any[] = [];
  loading = true;

  selectedBatch: any = null;

  // üîî Delete popup state
  showDeletePopup = false;

  constructor(
    private batchManage: BatchManageService,
    private dashboardApi: InstructorDashboardService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBatchWiseStats();
  }

  loadBatchWiseStats(): void {
    // üîπ Dashboard stats still used (NO BREAK)
    this.dashboardApi.getBatchWiseStats().subscribe({
      next: res => {
        this.batches = res;
        this.loading = false;
      },
      error: err => {
        console.error('Batch-wise stats error', err);
        this.loading = false;
      }
    });
  }

  // ü™ü Open batch popup
  openBatchPopup(batch: any): void {
    this.selectedBatch = batch;
  }

  closePopup(): void {
    this.selectedBatch = null;
    this.showDeletePopup = false;
  }

  // ‚úèÔ∏è Edit batch
  editBatch(): void {
    if (!this.selectedBatch) return;

    const batchId = this.selectedBatch.batchId;
    this.closePopup();

    this.router.navigate(['/instructor/edit-batch', batchId]);
  }

  // üóëÔ∏è Ask delete confirmation
  confirmDelete(): void {
    this.showDeletePopup = true;
  }

  // üóëÔ∏è Delete batch (FINAL)
  deleteBatch(): void {
    if (!this.selectedBatch) return;

    this.batchManage.deleteBatch(this.selectedBatch.batchId).subscribe({
      next: () => {
        this.closePopup();
        // ‚ùå no manual reload needed
        // ‚úÖ cache already refreshed in service
        this.loadBatchWiseStats();
      },
      error: err => {
        console.error(err);
      }
    });
  }
}
<div class="page-container">
  <h2 class="page-title">My Batches</h2>

  <!-- Loading -->
  <p *ngIf="loading" class="text-muted">Loading batches...</p>

  <!-- No Data -->
  <p *ngIf="!loading && batches.length === 0" class="text-muted">
    No batches found.
  </p>

  <!-- Batch Table -->
  <table *ngIf="!loading && batches.length > 0" class="table table-striped">
    <thead>
      <tr>
        <th>Batch Name</th>
        <th>Students</th>
        <th>Total Earnings</th>
      </tr>
    </thead>

    <tbody>
  <tr *ngFor="let b of batches">
    <td data-label="Batch Name">
      <span
        class="batch-name-link"
        (click)="openBatchPopup(b)"
        title="Click to manage batch"
      >
        {{ b.batchName }}
      </span>
    </td>

    <td data-label="Students">
      {{ b.studentsEnrolled }}
    </td>

    <td data-label="Total Earnings">
      ‚Çπ {{ b.totalEarnings }}
    </td>
  </tr>
</tbody>

  </table>
</div>

<!-- ü™ü BATCH ACTION POPUP -->
<div class="popup-backdrop" *ngIf="selectedBatch">
  <div class="popup-card">
    <h3>{{ selectedBatch.batchName }}</h3>

    <div class="popup-actions">
      <button class="btn-edit" (click)="editBatch()">
        ‚úèÔ∏è Edit Batch
      </button>

      <button class="btn-delete" (click)="deleteBatch()">
        üóëÔ∏è Delete Batch
      </button>

      <button class="btn-cancel" (click)="closePopup()">
        Cancel
      </button>
    </div>
  </div>
</div>
