import { Component, OnInit } from '@angular/core';
import { BatchService } from '../../services/batch.service';
import { Router } from '@angular/router';
import * as bootstrap from 'bootstrap';

@Component({
  selector: 'app-manage-batches',
  templateUrl: './manage-batches.component.html',
  styleUrls: ['./manage-batches.component.css']
})
export class ManageBatchesComponent implements OnInit {

  batches: any[] = [];
  editBatch: any = {};
  deleteBatchId: string = "";
  deleteBatchName: string = "";

  editModal: any;
  deleteModal: any;

  constructor(
    private batchApi: BatchService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBatches();
  }

  loadBatches() {
    const instructorId = localStorage.getItem("userId");

    this.batchApi.getBatchesByInstructor(instructorId!).subscribe({
      next: (res: any) => this.batches = res,
      error: err => console.error(err)
    });
  }

  openEdit(batch: any) {
    this.editBatch = { ...batch };
    this.editModal = new bootstrap.Modal(document.getElementById('editBatchModal')!);
    this.editModal.show();
  }

  updateBatch() {
    this.batchApi.updateBatch(this.editBatch.batchId, this.editBatch).subscribe({
      next: () => {
        this.editModal.hide();
        this.loadBatches();
        alert("Batch updated successfully!");
      },
      error: err => console.error(err)
    });
  }

  openDelete(batch: any) {
    this.deleteBatchId = batch.batchId;
    this.deleteBatchName = batch.batchName;
    this.deleteModal = new bootstrap.Modal(document.getElementById('deleteBatchModal')!);
    this.deleteModal.show();
  }

  deleteBatch() {
    this.batchApi.deleteBatch(this.deleteBatchId).subscribe({
      next: () => {
        this.deleteModal.hide();
        this.loadBatches();
        alert("Batch deleted successfully!");
      },
      error: err => console.error(err)
    });
  }
}
<div class="container py-4">

  <h2 class="page-title text-primary-custom mb-4">Manage Your Batches</h2>

  <!-- No batches message -->
  <div *ngIf="batches.length === 0" class="text-center text-muted mt-5">
    <i class="bi bi-info-circle fs-2"></i>
    <p class="mt-2">You haven't created any batches yet.</p>
    <button class="btn btn-primary-custom mt-3" routerLink="/instructor/create-batch">Create Batch</button>
  </div>

  <!-- Batches Table -->
  <div *ngIf="batches.length > 0" class="card card-custom p-4 shadow-sm">

    <table class="table table-striped">
      <thead>
        <tr>
          <th>Batch Name</th>
          <th>Start Date</th>
          <th>Students</th>
          <th style="width: 150px;">Actions</th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let batch of batches">
          <td>{{ batch.batchName }}</td>
          <td>{{ batch.startDate | date }}</td>
          <td>{{ batch.bookings?.length || 0 }}</td>

          <td>
            <button class="btn btn-sm btn-warning me-2" (click)="openEdit(batch)">
              Edit
            </button>

            <button class="btn btn-sm btn-danger" (click)="openDelete(batch)">
              Delete
            </button>
          </td>
        </tr>
      </tbody>
    </table>

  </div>

  <!-- EDIT MODAL -->
  <div class="modal fade" id="editBatchModal" tabindex="-1">
    <div class="modal-dialog">
      <div class="modal-content">

        <div class="modal-header">
          <h5 class="modal-title">Edit Batch</h5>
          <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
        </div>

        <div class="modal-body">
          <label class="form-label fw-bold">Batch Name</label>
          <input class="form-control mb-3" [(ngModel)]="editBatch.batchName">

          <label class="form-label fw-bold">Start Date</label>
          <input type="date" class="form-control" [(ngModel)]="editBatch.startDate">
        </div>

        <div class="modal-footer">
          <button class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>
          <button class="btn btn-primary-custom" (click)="updateBatch()">Update</button>
        </div>

      </div>
    </div>
  </div>

  <!-- DELETE CONFIRM MODAL -->
  <div class="modal fade" id="deleteBatchModal" tabindex="-1">
    <div class="modal-dialog modal-sm">
      <div class="modal-content">

        <div class="modal-header">
          <h5 class="modal-title text-danger">Delete Batch</h5>
          <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
        </div>

        <div class="modal-body text-center">
          <p>Are you sure you want to delete this batch?</p>
          <strong>{{ deleteBatchName }}</strong>
        </div>

        <div class="modal-footer justify-content-center">
          <button class="btn btn-secondary btn-sm" data-bs-dismiss="modal">Cancel</button>
          <button class="btn btn-danger btn-sm" (click)="deleteBatch()">Delete</button>
        </div>

      </div>
    </div>
  </div>

</div>
