import { Component, OnInit } from '@angular/core';
import { BookingService } from '../../services/booking.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-my-bookings',
  templateUrl: './my-bookings.component.html',
  styleUrls: ['./my-bookings.component.css']
})
export class MyBookingsComponent implements OnInit {

  bookings: any[] = [];

  constructor(
    private bookingApi: BookingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBookings();
  }

  loadBookings() {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      alert("Please login again.");
      this.router.navigate(['/login']);
      return;
    }

    this.bookingApi.getBookingsByUser(userId).subscribe({
      next: (res: any) => this.bookings = res,
      error: (err) => console.error(err)
    });
  }

  makePayment(booking: any) {
    // TODO: Replace with real payment screen / Razorpay
    alert(`Processing payment for ${booking.batch.batchName}`);

    this.router.navigate(['/user/payment'], {
      state: { bookingId: booking.bookingId, amount: booking.batch.service?.price }
    });
  }
}


<div class="container py-4">

  <h2 class="page-title text-primary-custom mb-4">My Bookings</h2>

  <!-- No bookings message -->
  <div *ngIf="bookings.length === 0" class="text-center text-muted mt-5">
    <i class="bi bi-info-circle fs-2"></i>
    <p class="mt-2">You have not booked any batches yet.</p>
    <button class="btn btn-primary-custom mt-3" routerLink="/user/book">Book a Batch</button>
  </div>

  <!-- Bookings list -->
  <div class="row g-4" *ngIf="bookings.length > 0">

    <div class="col-md-4" *ngFor="let booking of bookings">
      <div class="card card-custom p-3 shadow-sm">

        <h5 class="fw-bold">{{ booking.batch?.batchName }}</h5>

        <p class="text-muted mb-1">
          <i class="bi bi-person-badge"></i>
          Instructor: {{ booking.batch?.instructor?.name }}
        </p>

        <p class="text-muted mb-1">
          <i class="bi bi-calendar-event"></i>
          Start Date: {{ booking.batch?.startDate | date }}
        </p>

        <p class="mb-1">
          <i class="bi bi-clock-history"></i>
          Booked on: {{ booking.bookingDate | date }}
        </p>

        <!-- Status Badge -->
        <span class="badge mt-2"
              [ngClass]="{
                'bg-success': booking.status === 'Confirmed',
                'bg-warning text-dark': booking.status === 'Pending',
                'bg-danger': booking.status === 'Cancelled'
              }">
          {{ booking.status }}
        </span>

        <!-- Payment Button -->
        <button *ngIf='booking.status === "Pending"'
                class="btn btn-primary-custom w-100 mt-3"
                (click)="makePayment(booking)">
          Pay Now
        </button>

      </div>
    </div>

  </div>

</div>
