import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';

// Layout
import { MainLayoutComponent } from './shared/layout/main-layout/main-layout.component';

// Auth
import { LoginComponent } from './auth/login/login.component';
import { SelectRoleComponent } from './auth/select-role/select-role.component';

// User Components
import { UserDashboardComponent } from './user/dashboard/dashboard.component';
import { BookBatchComponent } from './user/book-batch/book-batch.component';
import { MyBookingsComponent } from './user/my-bookings/my-bookings.component';
import { PaymentComponent } from './user/payment/payment.component';

// Instructor Components
import { InstructorDashboardComponent } from './instructor/dashboard/dashboard.component';
import { CreateBatchComponent } from './instructor/create-batch/create-batch.component';
import { ManageBatchesComponent } from './instructor/manage-batches/manage-batches.component';
import { ViewBookingsComponent } from './instructor/view-bookings/view-bookings.component';

// Shared
import { ProfileComponent } from './shared/profile/profile.component';
import { HomeComponent } from './pages/home/home.component';

const routes: Routes = [


  // Public Routes
  { path: '', redirectTo: 'login', pathMatch: 'full' },
  { path: 'login', component: LoginComponent },
  { path: 'select-role', component: SelectRoleComponent },

  // Protected Routes under Main Layout
  {
    path: '',
    component: MainLayoutComponent,
    children: [

      { path: 'home', component: HomeComponent },
      // USER ROUTES
      { path: 'user/dashboard', component: UserDashboardComponent },
      { path: 'user/book-batch', component: BookBatchComponent },
      { path: 'user/my-bookings', component: MyBookingsComponent },
      { path: 'user/payment', component: PaymentComponent },

      // INSTRUCTOR ROUTES
      { path: 'instructor/dashboard', component: InstructorDashboardComponent },
      { path: 'instructor/create-batch', component: CreateBatchComponent },
      { path: 'instructor/manage-batches', component: ManageBatchesComponent },
      { path: 'instructor/view-bookings', component: ViewBookingsComponent },

      // SHARED
      { path: 'profile', component: ProfileComponent }
    ]
  },

 { path: '', redirectTo: 'home', pathMatch: 'full' },

];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule {}
