import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';

// Layouts
import { PublicLayoutComponent } from './layouts/public-layout/public-layout.component';
import { AuthLayoutComponent } from './layouts/auth-layout/auth-layout.component';
import { UserLayoutComponent } from './layouts/user-layout/user-layout.component';
import { InstructorLayoutComponent } from './layouts/instructor-layout/instructor-layout.component';

// Auth Components
import { LoginComponent } from './modules/auth/login/login.component';
import { SelectRoleComponent } from './modules/auth/select-role/select-role.component';

const routes: Routes = [

  // üåç PUBLIC (Homepage, landing, etc.)
  {
    path: '',
    component: PublicLayoutComponent,
    children: [
      {
        path: '',
        loadChildren: () =>
          import('./modules/public/public.module')
            .then(m => m.PublicModule)
      }
    ]
  },

  // üîê AUTH
  {
    path: 'auth',
    component: AuthLayoutComponent,
    children: [
      { path: '', redirectTo: 'login', pathMatch: 'full' },
      { path: 'login', component: LoginComponent },
      { path: 'select-role', component: SelectRoleComponent }
    ]
  },

  // üë§ USER
  {
    path: 'user',
    component: UserLayoutComponent,
    children: [
      {
        path: '',
        loadChildren: () =>
          import('./modules/user/user.module')
            .then(m => m.UserModule)
      }
    ]
  },

  // üßë‚Äçüè´ INSTRUCTOR
  {
    path: 'instructor',
    component: InstructorLayoutComponent,
    children: [
      {
        path: '',
        loadChildren: () =>
          import('./modules/instructor/instructor.module')
            .then(m => m.InstructorModule)
      }
    ]
  },

  // ‚ùå Fallback
  { path: '**', redirectTo: '' }
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule {}
