import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']

})
export class UserDashboardComponent {

  constructor(private router: Router) {}

  goTo(page: string) {
    switch (page) {
      case 'book':
        this.router.navigate(['/user/book']);
        break;

      case 'bookings':
        this.router.navigate(['/user/bookings']);
        break;

      case 'services':
        this.router.navigate(['/services']);
        break;
    }
  }

}
<section class="hero">
  <div class="hero-left">
    <h1>10-Day Masterclass for Hair Stylists</h1>
    <p>Learn advanced techniques from industry experts.</p>

    <button routerLink="/user/book" class="book-btn">Book Now</button>
  </div>

  <div class="hero-img-box">
    <img src="assets/home-model.jpg" alt="Hair Stylist" />
  </div>
</section>

<section class="batches">
  <h2>Upcoming Masterclass Batches</h2>

  <div class="batch-card">
    <h3>May 15 – May 24</h3>
    <p>10-Day Course</p>

    <div class="batch-info">
      <span>5 seats remaining</span>
      <button routerLink="/user/book" class="view-btn">View Batches</button>
    </div>
  </div>
</section>
