import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  constructor(private router: Router) {}

  showNavbar(): boolean {
    return !(
      this.router.url.includes('login') ||
      this.router.url.includes('select-role')
    );
  }

  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  getUserName(): string {
    return localStorage.getItem('fullName') || 'Profile';
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
<nav class="navbar navbar-expand-lg main-navbar" *ngIf="showNavbar()">
  <div class="container-fluid">

    <a class="navbar-brand fw-bold text-white" routerLink="/">
      <i class="bi bi-scissors me-1"></i> Hairstylist App
    </a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarSupportedContent">

      <ul class="navbar-nav me-auto mb-2 mb-lg-0">

        <!-- USER MENU -->
        <li class="nav-item" *ngIf="isUser()">
          <a routerLink="/user/dashboard" class="nav-link">Dashboard</a>
        </li>
        <li class="nav-item" *ngIf="isUser()">
          <a routerLink="/user/book-batch" class="nav-link">Book Batch</a>
        </li>
        <li class="nav-item" *ngIf="isUser()">
          <a routerLink="/user/my-bookings" class="nav-link">My Bookings</a>
        </li>

        <!-- INSTRUCTOR MENU -->
        <li class="nav-item" *ngIf="isInstructor()">
          <a routerLink="/instructor/dashboard" class="nav-link">Dashboard</a>
        </li>
        <li class="nav-item" *ngIf="isInstructor()">
          <a routerLink="/instructor/manage-batches" class="nav-link">Manage Batches</a>
        </li>
        <li class="nav-item" *ngIf="isInstructor()">
          <a routerLink="/instructor/view-bookings" class="nav-link">Bookings</a>
        </li>

      </ul>

      <!-- PROFILE DROPDOWN -->
      <div class="dropdown">
        <button class="btn btn-light dropdown-toggle d-flex align-items-center"
                type="button" data-bs-toggle="dropdown">

          <i class="bi bi-person-circle fs-4 me-2"></i>
          {{ getUserName() }}

        </button>

        <ul class="dropdown-menu dropdown-menu-end">
          <li><a class="dropdown-item" routerLink="/profile">My Profile</a></li>
          <li><hr class="dropdown-divider"></li>
          <li><button class="dropdown-item text-danger" (click)="logout()">Logout</button></li>
        </ul>
      </div>

    </div>
  </div>
</nav>
