// src/app/services/users/user-profile.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { API } from 'src/app/core/api/api-constants.service';
import { UserProfile } from 'src/app/interfaces/user-profile';

@Injectable({ providedIn: 'root' })
export class UserProfileService {

  constructor(private http: HttpClient) {}

  // ðŸ”¹ GET logged-in user profile
  getProfile(): Observable<UserProfile> {
    return this.http.get<UserProfile>(API.userProfile);
  }

  // ðŸ”¹ UPDATE logged-in user profile (name, mobile, etc.)
 updateProfile(data: UserProfile): Observable<void> {
  return this.http.put<void>(API.userProfile, data);
}


  // ðŸ”¹ UPLOAD profile photo
  uploadPhoto(formData: FormData): Observable<{ photoUrl: string }> {
    return this.http.post<{ photoUrl: string }>(
      `${API.userProfile}/photo`,
      formData
    );
  }
}
