import { Component, HostListener, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import {
  AuthStorageService,
  UserRole
} from '../../core/auth/auth-storage.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnDestroy {

  role: UserRole = null;
  menuOpen = false;
  searchText = '';
  hideSearch = false;

  private roleSub!: Subscription;
  private lastScrollTop = 0;

  constructor(
    private router: Router,
    private authStorage: AuthStorageService
  ) {
    // ðŸ” React to login / logout / role change
    this.roleSub = this.authStorage.role$.subscribe(role => {
      this.role = role;
    });
  }

  // ðŸ§¹ Cleanup
  ngOnDestroy(): void {
    this.roleSub?.unsubscribe();
  }

  // ðŸ”½ Hide search on scroll down
  @HostListener('window:scroll', [])
  onScroll(): void {
    const scrollTop =
      window.pageYOffset || document.documentElement.scrollTop;

    this.hideSearch = scrollTop > this.lastScrollTop && scrollTop > 80;
    this.lastScrollTop = scrollTop <= 0 ? 0 : scrollTop;
  }

  // ðŸ” LOGIN
  onLogin(): void {
    this.router.navigate(['/auth/login']);
  }

  // ðŸ“Š DASHBOARD
  goToDashboard(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/dashboard']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/dashboard']);
    }
  }

  // ðŸ—‚ï¸ MANAGE BATCHES
  goToManageBatches(): void {
    if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/batches']);
    }
  }

  // ðŸ“– VIEW BOOKINGS
  goToViewBookings(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/bookings']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/bookings']);
    }
  }

  // ðŸ“š BOOK BATCH (User only)
  goToBookBatch(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/book-batch']); // âœ… consistent
    }
  }

  // ðŸ” GLOBAL SEARCH (PUBLIC)
  onSearch(): void {
    const query = this.searchText?.trim();
    if (!query) return;

    this.router.navigate(['/search'], {
      queryParams: { q: query }
    });

    this.searchText = '';
  }

  // ðŸ‘¤ PROFILE
  goToProfile(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/profile']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/profile']);
    }
  }

  // ðŸšª LOGOUT
  logout(): void {
    this.authStorage.clearAll();
    this.router.navigate(['/']);
  }
}
