import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { API } from 'src/app/core/api/api-constants.service';
import { BookingUserService } from 'src/app/services/bookings/booking-user.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css'],
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  batches: any[] = [];
  selectedService: any = null;

  loading = true;
  activeBatchId: string | null = null;
  studentMobile = '';
  processing = false;
  FILE_BASE = 'https://localhost:7154';

  constructor(
    private http: HttpClient,
    private router: Router,
    private bookingApi: BookingUserService
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  // ðŸ”¹ Load all services
  loadServices(): void {
    this.http.get<any[]>(API.services).subscribe({
      next: res => {
        this.services = res;
        this.loading = false;
      },
      error: () => this.loading = false
    });
  }

  // ðŸ”¹ Click service card
  selectService(service: any): void {
    if (this.selectedService?.serviceId === service.serviceId) {
      this.selectedService = null;
      this.batches = [];
      return;
    }

    this.selectedService = service;
    this.activeBatchId = null;
    this.studentMobile = '';

    this.loadBatches(service.serviceId);
  }

  // ðŸ”¹ Load batches by service
  loadBatches(serviceId: string): void {
    this.http
      .get<any[]>(`${API.batches}/service/${serviceId}`)
      .subscribe(res => {
        this.batches = res;

        // attach instructor preview to service card
        if (res.length > 0) {
          this.selectedService.instructor = res[0].instructor;
        }
      });
  }

  openMobileInput(batchId: string): void {
    this.activeBatchId = batchId;
    this.studentMobile = '';
  }

  confirmBooking(batchId: string): void {
    if (this.processing || this.studentMobile.length !== 10) return;

    const userId = localStorage.getItem('userId');
    if (!userId) return;

    this.processing = true;

    this.bookingApi.createBooking({
      userId,
      batchId,
      studentMobile: this.studentMobile
    }).subscribe({
      next: (res: any) => {
        this.processing = false;
        this.router.navigate(['/user/payment', res.bookingId]);
      },
      error: () => this.processing = false
    });
  }

  // âœ… REQUIRED METHOD FOR INSTRUCTOR IMAGE
 getInstructorPhoto(instructor: any): string {
  if (!instructor || !instructor.photoUrl) {
    return 'assets/user.png';
  }

  // If backend already sends full URL
  if (instructor.photoUrl.startsWith('http')) {
    return instructor.photoUrl;
  }

  // âœ… Correct: prefix with API base URL
  return `${this.FILE_BASE}${instructor.photoUrl}`;
}

}
<div class="services-grid">
  <div
    class="service-card"
    *ngFor="let service of services"
    (click)="selectService(service)"
  >
    <!-- Service Title -->
    <h3 class="service-title">{{ service.name }}</h3>

    <!-- Instructor -->
    <div class="instructor-info" *ngIf="service.instructor">
      <img
        [src]="getInstructorPhoto(service.instructor)"
        class="instructor-photo"
        alt="Instructor Photo"
      />

      <div>
        <p class="instructor-name">{{ service.instructor.name }}</p>
        <p class="instructor-mobile">
          ðŸ“ž {{ service.instructor.contactMobile }}
        </p>

        <div class="rating">
          â˜… â˜… â˜… â˜… â˜† <span class="rating-value">4.5</span>
        </div>
      </div>
    </div>

    <a class="view-link">View Available Batches â†’</a>

    <!-- ðŸ”½ BATCHES OPEN JUST BELOW THIS SERVICE -->
    <!-- Batches wrapper -->
    <div
      class="batches-wrapper"
      *ngIf="selectedService?.serviceId === service.serviceId"
      (click)="$event.stopPropagation()"
    >
      <h4 class="batches-title">Batches for {{ service.name }}</h4>

      <!-- ðŸ”¹ NEW LINE LAYOUT -->
      <div class="batches-row">
        <div *ngFor="let batch of batches" class="batch-card">
          <strong class="batch-name">
            {{ batch.batchName }}
          </strong>

          <p>Start Date: {{ batch.startDate | date }}</p>
          <p class="price">Price: â‚¹{{ batch.price }}</p>

          <button (click)="openMobileInput(batch.batchId)">Book Now</button>
        </div>
      </div>
    </div>
  </div>
</div>
