import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ServiceQueryService } from 'src/app/services/services/service-query.service';


@Component({
  selector: 'app-search-results',
  templateUrl: './search-results.component.html'
})
export class SearchResultsComponent implements OnInit {

  query = '';
  services: any[] = [];
  instructors: any[] = [];
  batches: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private searchService: ServiceQueryService
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      this.query = params['q'];

      if (this.query) {
        this.search();
      }
    });
  }

  search(): void {
    this.searchService.search(this.query).subscribe({
      next: (res: { services: never[]; instructors: never[]; batches: never[]; }) => {
        console.log('Search result:', res);

        this.services = res.services || [];
        this.instructors = res.instructors || [];
        this.batches = res.batches || [];
      },
      error: (err) => {
        console.error('Search failed', err);
      }
    });
  }

  onBook(batch: any) {
    const token = localStorage.getItem('token');

    if (!token) {
      this.router.navigate(['/auth/login'], {
        queryParams: { redirect: '/search-results?q=' + this.query }
      });
    } else {
      this.router.navigate(['/user/book-batch'], {
        state: { batch }
      });
    }
  }
}
<div class="batch-card" *ngFor="let batch of batches">
  <h4>{{ batch.batchName }}</h4>
  <p>Start: {{ batch.startDate | date }}</p>

  <button (click)="onBook(batch)">
    Book Now
  </button>
</div>
