import { Component, OnInit } from '@angular/core';
import { BatchService } from '../../services/batch.service';
import { ServiceService } from 'src/app/services/service.service';
import { Router } from '@angular/router';
import * as bootstrap from 'bootstrap';

@Component({
  selector: 'app-manage-batches',
  templateUrl: './manage-batches.component.html',
  styleUrls: ['./manage-batches.component.css']
})
export class ManageBatchesComponent implements OnInit {

  batches: any[] = [];
  services: any[] = [];

  editBatch: any = {};
  deleteBatchId: string = "";
  deleteBatchName: string = "";

  editModal: any;
  deleteModal: any;

  constructor(
    private batchApi: BatchService,
    private serviceApi: ServiceService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
    this.loadBatches();
  }

  // Load all services for dropdown in edit modal
  loadServices() {
    this.serviceApi.getAllServices().subscribe({
      next: (res) => this.services = res,
      error: (err) => console.error("Service load error:", err)
    });
  }

  // Load instructor's batches
  loadBatches() {
    const instructorId = localStorage.getItem("instructorId");

    if (!instructorId) {
      alert("Please login again");
      this.router.navigate(['/login']);
      return;
    }

    this.batchApi.getBatchesByInstructor(instructorId).subscribe({
      next: (res: any) => this.batches = res,
      error: err => console.error("Batch load error:", err)
    });
  }

  // Open Edit Modal
  openEdit(batch: any) {
    this.editBatch = {
      batchId: batch.batchId,
      batchName: batch.batchName,
      description: batch.description,
      price: batch.price,
      startDate: batch.startDate?.split("T")[0],
      instructorId: batch.instructorId,
      serviceId: batch.serviceId
    };

    this.editModal = new bootstrap.Modal(document.getElementById('editBatchModal')!);
    this.editModal.show();
  }

  // Save updated batch
  updateBatch() {
    const payload = {
      batchId: this.editBatch.batchId,
      batchName: this.editBatch.batchName,
      description: this.editBatch.description,
      price: this.editBatch.price,
      startDate: this.editBatch.startDate,
      instructorId: this.editBatch.instructorId,
      serviceId: this.editBatch.serviceId
    };

    this.batchApi.updateBatch(this.editBatch.batchId, payload).subscribe({
      next: () => {
        this.editModal.hide();
        this.loadBatches();
        alert("Batch updated successfully!");
      },
      error: err => console.error("Update error:", err)
    });
  }

  // Open Delete Confirmation
  openDelete(batch: any) {
    this.deleteBatchId = batch.batchId;
    this.deleteBatchName = batch.batchName;

    this.deleteModal = new bootstrap.Modal(document.getElementById('deleteBatchModal')!);
    this.deleteModal.show();
  }

  // Delete batch
  deleteBatch() {
    this.batchApi.deleteBatch(this.deleteBatchId).subscribe({
      next: () => {
        this.deleteModal.hide();
        this.loadBatches();
        alert("Batch deleted successfully!");
      },
      error: err => console.error("Delete error:", err)
    });
  }

}
<div class="container mt-4">

  <h2 class="fw-bold mb-3">Manage Your Batches</h2>

  <!-- Loader -->
  <div class="text-center" *ngIf="batches.length === 0">
    <p class="text-muted">No batches created yet.</p>
  </div>

  <div class="row g-4">
    <div class="col-md-4" *ngFor="let batch of batches">
      <div class="card shadow-sm p-3 hover-card">

        <h5 class="fw-bold">{{ batch.batchName }}</h5>

        <p class="text-muted small mb-1">
          {{ batch.description }}
        </p>

        <p class="mb-1">
          <strong>Service:</strong> {{ batch.service?.name }}
        </p>

        <p class="mb-1">
          <strong>Price:</strong> ₹{{ batch.price }}
        </p>

        <p class="mb-2">
          <strong>Start Date:</strong> {{ batch.startDate | date }}
        </p>

        <div class="d-flex gap-2 mt-2">
          <button class="btn btn-primary w-50" (click)="openEdit(batch)">Edit</button>
          <button class="btn btn-danger w-50" (click)="openDelete(batch)">Delete</button>
        </div>

      </div>
    </div>
  </div>
</div>


<div class="modal fade" id="editBatchModal" tabindex="-1">
  <div class="modal-dialog modal-lg">
    <div class="modal-content p-3">

      <div class="modal-header">
        <h5 class="modal-title">Edit Batch</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
      </div>

      <div class="modal-body">

        <div class="mb-3">
          <label class="form-label fw-semibold">Service</label>
          <select class="form-select" [(ngModel)]="editBatch.serviceId">
            <option *ngFor="let s of services" [value]="s.serviceId">{{ s.name }}</option>
          </select>
        </div>

        <div class="mb-3">
          <label class="form-label fw-semibold">Batch Name</label>
          <input type="text" class="form-control" [(ngModel)]="editBatch.batchName">
        </div>

        <div class="mb-3">
          <label class="form-label fw-semibold">Description</label>
          <textarea rows="4" class="form-control" [(ngModel)]="editBatch.description"></textarea>
        </div>

        <div class="mb-3">
          <label class="form-label fw-semibold">Price (₹)</label>
          <input type="number" class="form-control" [(ngModel)]="editBatch.price">
        </div>

        <div class="mb-3">
          <label class="form-label fw-semibold">Start Date</label>
          <input type="date" class="form-control" [(ngModel)]="editBatch.startDate">
        </div>

      </div>

      <div class="modal-footer">
        <button class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>
        <button class="btn btn-primary" (click)="updateBatch()">Save Changes</button>
      </div>

    </div>
  </div>
</div>


<div class="modal fade" id="deleteBatchModal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content p-3">

      <div class="modal-header">
        <h5 class="modal-title">Delete Batch</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
      </div>

      <div class="modal-body">
        Are you sure you want to delete the batch:
        <strong>{{ deleteBatchName }}</strong>?
      </div>

      <div class="modal-footer">
        <button class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>
        <button class="btn btn-danger" (click)="deleteBatch()">Delete</button>
      </div>

    </div>
  </div>
</div>
