import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BatchService } from '../../services/batch.service';

@Component({
  selector: 'app-create-batch',
  templateUrl: './create-batch.component.html',
  styleUrls: ['./create-batch.component.css']
})
export class CreateBatchComponent {

  batchName: string = "";
  startDate: string = "";
  price: number = 0;   // ⭐ New field

  constructor(
    private batchApi: BatchService,
    private router: Router
  ) {}

  createBatch() {
    const instructorId = localStorage.getItem("userId");

    if (!instructorId) {
      alert("Please login again.");
      this.router.navigate(['/login']);
      return;
    }

    // ⭐ DO NOT SEND batchId — backend generates it
    const newBatch = {
      instructorId: instructorId,
      batchName: this.batchName,
      startDate: new Date(this.startDate), // Convert to Date
      price: this.price
    };

    this.batchApi.createBatch(newBatch).subscribe({
      next: () => {
        alert("Batch created successfully!");
        this.router.navigate(['/instructor/manage-batches']);
      },
      error: (err) => {
        console.error(err);
        alert("Failed to create batch.");
      }
    });
  }
}
<div class="container mt-4">
  <h2 class="fw-bold mb-3">Create Batch</h2>

  <div class="card p-4 shadow-sm">

    <div class="mb-3">
      <label class="form-label">Batch Name</label>
      <input type="text" class="form-control" [(ngModel)]="batchName">
    </div>

    <div class="mb-3">
      <label class="form-label">Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="startDate">
    </div>

    <div class="mb-3">
      <label class="form-label">Price ₹</label>
      <input type="number" class="form-control" [(ngModel)]="price">
    </div>

    <button class="btn btn-primary w-100" (click)="createBatch()">
      Create Batch
    </button>

  </div>
</div>
