import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BatchService } from 'src/app/services/batch.service';

@Component({
  selector: 'app-edit-batch',
  templateUrl: './edit-batch.component.html',
  styleUrls: ['./edit-batch.component.css']
})
export class EditBatchComponent implements OnInit {

  batchId: string = "";
  batchName: string = "";
  startDate: string = "";
  price: number = 0;   // ⭐ NEW FIELD

  isLoading = true;

  constructor(
    private route: ActivatedRoute,
    private batchApi: BatchService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.batchId = this.route.snapshot.params['id'];
    this.loadBatchDetails();
  }

  loadBatchDetails() {
    this.batchApi.getBatchById(this.batchId).subscribe({
      next: (batch: any) => {
        this.batchName = batch.batchName;
        this.startDate = batch.startDate.split("T")[0]; 
        this.price = batch.price;     // ⭐ LOAD PRICE
        this.isLoading = false;
      },
      error: () => {
        alert("Unable to load batch details.");
        this.router.navigate(['/instructor/manage-batches']);
      }
    });
  }

  updateBatch() {
    const updated = {
      batchId: this.batchId,
      batchName: this.batchName,
      startDate: new Date(this.startDate),
      price: this.price           // ⭐ SEND PRICE
    };

    this.batchApi.updateBatch(this.batchId, updated).subscribe({
      next: () => {
        alert("Batch updated successfully!");
        this.router.navigate(['/instructor/manage-batches']);
      },
      error: (err) => console.error(err)
    });
  }
}
<div class="container mt-4">

  <h2 class="fw-bold mb-3">Edit Batch</h2>

  <div *ngIf="isLoading" class="text-center">
    <div class="spinner-border text-primary"></div>
    <p>Loading...</p>
  </div>

  <div *ngIf="!isLoading" class="card p-4 shadow-sm">

    <div class="mb-3">
      <label class="form-label">Batch Name</label>
      <input type="text" class="form-control" [(ngModel)]="batchName">
    </div>

    <div class="mb-3">
      <label class="form-label">Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="startDate">
    </div>

    <div class="mb-3">
      <label class="form-label">Price (₹)</label>
      <input type="number" class="form-control" [(ngModel)]="price">
    </div>

    <button class="btn btn-primary w-100" (click)="updateBatch()">
      Save Changes
    </button>

  </div>

</div>
