import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']

})
export class UserDashboardComponent {

  constructor(private router: Router) {}

  goTo(page: string) {
    switch (page) {
      case 'book':
        this.router.navigate(['/user/book']);
        break;

      case 'bookings':
        this.router.navigate(['/user/bookings']);
        break;

      case 'services':
        this.router.navigate(['/services']);
        break;
    }
  }

}
<div class="container py-4">

  <!-- Page Title -->
  <h2 class="page-title text-primary-custom mb-4">
    Welcome User ðŸ‘‹
  </h2>

  <!-- Quick Actions -->
  <div class="row g-4">

    <!-- Book Batch -->
    <div class="col-md-4">
      <div class="card card-custom text-center p-3 hover-card" (click)="goTo('book')">
        <i class="bi bi-calendar-check fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">Book Batch</h5>
        <p class="text-muted">Browse available batches and enroll.</p>
      </div>
    </div>

    <!-- My Bookings -->
    <div class="col-md-4">
      <div class="card card-custom text-center p-3 hover-card" (click)="goTo('bookings')">
        <i class="bi bi-journal-text fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">My Bookings</h5>
        <p class="text-muted">View your batch enrollment and payment status.</p>
      </div>
    </div>

    <!-- Services -->
    <div class="col-md-4">
      <div class="card card-custom text-center p-3 hover-card" (click)="goTo('services')">
        <i class="bi bi-scissors fs-1 text-primary-custom mb-2"></i>
        <h5 class="fw-bold">Services</h5>
        <p class="text-muted">Explore hairstyling courses and masterclasses.</p>
      </div>
    </div>

  </div>

</div>
