import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-select-role',
  templateUrl: './select-role.component.html',
  styleUrls: ['./select-role.component.css']
})
export class SelectRoleComponent {

  selectedRole: string | null = null;

  // ✅ NEW: store instructor mobile
  contactMobile: string = '';

  constructor(
    private auth: AuthService,
    private router: Router,
    private http: HttpClient
  ) {}

  selectRole(role: string) {
    this.selectedRole = role;
  }

  continue() {
    const userId = localStorage.getItem("userId");

    if (!this.selectedRole) return;

    localStorage.setItem("role", this.selectedRole);

    // -------------------------------
    // INSTRUCTOR FLOW
    // -------------------------------
    if (this.selectedRole === 'Instructor') {

      // ✅ VALIDATION (REQUIRED)
      if (!this.contactMobile || this.contactMobile.trim().length < 10) {
        alert("Please enter a valid mobile number");
        return;
      }

      // ✅ FIXED PAYLOAD (matches backend DTO)
      const body = {
        userId: userId,
        contactMobile: this.contactMobile
      };

      this.http.post("https://localhost:7154/api/instructors/create", body)
        .subscribe({
          next: (res: any) => {
            localStorage.setItem("instructorId", res.instructorId);
            this.router.navigate(['/home']);
          },
          error: err => {
            console.error("Instructor create error:", err.error);
            alert("Failed to create instructor");
          }
        });

    } 
    // -------------------------------
    // STUDENT FLOW (UNCHANGED)
    // -------------------------------
    else {
      this.router.navigate(['/home']);
    }
  }
}
<div class="container-fluid d-flex justify-content-center align-items-center min-vh-100 role-bg">

  <div class="card role-main-card shadow-lg p-4 gradient-card">

    <h3 class="text-center fw-bold mb-2 text-primary-custom">Choose Your Role</h3>
    <p class="text-center text-muted mb-4">Select how you want to continue</p>

    <div class="row g-3 justify-content-center">

      <!-- USER CARD -->
      <div class="col-12 col-md-6">
        <div class="role-box p-3 shadow-sm soft-card"
             [class.active]="selectedRole === 'User'"
             (click)="selectRole('User')">

          <div class="text-center">
            <i class="bi bi-person-circle role-icon mb-2 text-primary-custom"></i>
            <h5 class="fw-bold">Student</h5>
            <p class="small text-muted">Book batches and manage your learning.</p>
          </div>

        </div>
      </div>

      <!-- INSTRUCTOR CARD -->
      <div class="col-12 col-md-6">
        <div class="role-box p-3 shadow-sm soft-card"
             [class.active]="selectedRole === 'Instructor'"
             (click)="selectRole('Instructor')">

          <div class="text-center">
            <i class="bi bi-person-workspace role-icon mb-2 text-primary-custom"></i>
            <h5 class="fw-bold">Instructor</h5>
            <p class="small text-muted">Create batches and manage bookings.</p>
          </div>

        </div>
      </div>

    </div>

    <!-- ✅ MOBILE INPUT (ONLY FOR INSTRUCTOR) -->
    <div *ngIf="selectedRole === 'Instructor'" class="mt-4">
      <label class="form-label fw-semibold">Mobile Number<span class="text-danger"> *</span></label>
      <input
        type="tel"
        class="form-control"
        placeholder="Enter your mobile number"
        [(ngModel)]="contactMobile"
        maxlength="10"
      />
      <small class="text-muted">
        This number will be visible to students only after confirmed booking.
      </small>
    </div>

    <!-- Continue button -->
    <button 
      class="btn btn-primary-custom w-100 mt-4"
      [disabled]="!selectedRole"
      (click)="continue()">
      Continue
    </button>

  </div>

</div>
