import { Component, OnInit } from '@angular/core';
import { UserProfileService } from
  'src/app/services/users/user-profile.service';
import { UserProfile } from
  'src/app/interfaces/user-profile';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {

  API_BASE = 'https://localhost:7154';
  profile!: UserProfile;
  loading = true;
  editMode = false;
  uploadingPhoto = false;

  // ðŸ”” Popup state
  showPopup = false;
  popupMessage = '';
  popupType: 'success' | 'error' = 'error';

  constructor(private profileApi: UserProfileService) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  // ðŸ”¹ Load profile
  loadProfile(): void {
    this.profileApi.getProfile().subscribe({
      next: (res) => {
        this.profile = res;
        this.loading = false;
      },
      error: (err) => {
        console.error('Profile load failed', err);
        this.loading = false;
        this.openPopup('Failed to load profile', 'error');
      }
    });
  }

  // ðŸ”¹ Save profile
  saveProfile(): void {
    this.profileApi.updateProfile(this.profile).subscribe({
      next: () => {
        this.editMode = false;
        this.openPopup('Profile updated successfully', 'success');
      },
      error: err => {
        console.error(err);
        this.openPopup('Profile update failed', 'error');
      }
    });
  }

  // ðŸ”¹ Upload profile photo
  onPhotoSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (!input.files || input.files.length === 0) return;

    const file = input.files[0];

    if (!file.type.startsWith('image/')) {
      this.openPopup('Please select a valid image file', 'error');
      return;
    }

    if (file.size > 2 * 1024 * 1024) {
      this.openPopup('Image size must be under 2MB', 'error');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    this.profileApi.uploadPhoto(formData).subscribe({
      next: (res: any) => {
        this.profile.photoUrl = res.photoUrl + '?v=' + Date.now();
        this.openPopup('Profile photo updated', 'success');
      },
      error: () => this.openPopup('Photo upload failed', 'error')
    });
  }

  // ðŸ”” Popup helpers
  openPopup(message: string, type: 'success' | 'error'): void {
    this.popupMessage = message;
    this.popupType = type;
    this.showPopup = true;
  }

  closePopup(): void {
    this.showPopup = false;
  }
}
<div class="profile-page">
  <h2 class="page-title">My Profile</h2>

  <p *ngIf="loading" class="text-muted">Loading profile...</p>

  <div *ngIf="!loading && profile" class="profile-card">

    <div class="profile-header">
      <div class="photo-box">
        <img
          class="profile-avatar"
          [src]="
            profile?.photoUrl
              ? API_BASE + profile.photoUrl
              : 'assets/Images/default-avatar.png'
          "
          alt="User Photo"
        />

        <label class="upload-btn">
          ðŸ“· Change Photo
          <input
            type="file"
            accept="image/png,image/jpeg,image/jpg,image/webp"
            (change)="onPhotoSelected($event)"
            hidden
          />
        </label>
      </div>

      <div class="profile-basic">
        <h3>{{ profile.fullName }}</h3>
        <p class="email">{{ profile.email }}</p>
      </div>

      <button class="edit-btn" (click)="editMode = !editMode">
        {{ editMode ? "Cancel" : "Edit Profile" }}
      </button>
    </div>

    <div class="profile-body" *ngIf="!editMode">
      <div class="info-row">
        <label>Full Name</label>
        <span>{{ profile.fullName }}</span>
      </div>

      <div class="info-row">
        <label>Email</label>
        <span>{{ profile.email }}</span>
      </div>

      <div class="info-row">
        <label>Mobile</label>
        <span>{{ profile.mobile || "Not added" }}</span>
      </div>
    </div>

    <form *ngIf="editMode" class="profile-body" (ngSubmit)="saveProfile()">
      <div class="info-row">
        <label>Full Name</label>
        <input
          type="text"
          [(ngModel)]="profile.fullName"
          name="fullName"
          required
        />
      </div>

      <div class="info-row">
        <label>Mobile</label>
        <input
          type="tel"
          [(ngModel)]="profile.mobile"
          name="mobile"
          placeholder="Enter mobile number"
        />
      </div>

      <div class="actions">
        <button type="submit" class="save-btn">Save Changes</button>
      </div>
    </form>
  </div>
</div>

<!-- ðŸ”” Profile Popup -->
<div class="popup-overlay" *ngIf="showPopup">
  <div class="popup-modal">
    <h3 [ngClass]="popupType">
      {{ popupType === 'success' ? 'Success' : 'Error' }}
    </h3>
    <p>{{ popupMessage }}</p>
    <button class="popup-btn" (click)="closePopup()">OK</button>
  </div>
</div>
