import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { API } from './endpoints.service';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class BatchService {

  constructor(private http: HttpClient) {}

  // Get batches for selected service
  getAllBatches(serviceId: string): Observable<any> {
    return this.http.get(`${API.batches}/service/${serviceId}`);
  }

  // Get batches created by instructor
  getBatchesByInstructor(instructorId: string): Observable<any> {
    return this.http.get(`${API.batches}/instructor/${instructorId}`);
  }

  // Create a new batch
  createBatch(data: any): Observable<any> {
    return this.http.post(API.batches, data);
  }

  // Update a batch
  updateBatch(batchId: string, data: any): Observable<any> {
    return this.http.put(`${API.batches}/${batchId}`, data);
  }

  // Delete a batch
  deleteBatch(batchId: string): Observable<any> {
    return this.http.delete(`${API.batches}/${batchId}`);
  }
  getBatchById(id: string) {
  return this.http.get(`${API.batches}/${id}`);
}
}
