import { Component, OnInit } from '@angular/core';
import { BookingInstructorService } from '../../../services/bookings/booking-instructor.service';

@Component({
  selector: 'app-instructor-bookings',
  templateUrl: './bookings.component.html',
  styleUrls: ['./bookings.component.css']
})
export class InstructorBookingsComponent implements OnInit {

  bookings: any[] = [];
  loading = true;

  constructor(
    private bookingApi: BookingInstructorService
  ) {}

  ngOnInit(): void {
    this.loadInstructorBookings();
  }

  loadInstructorBookings(): void {
    this.bookingApi.getMyBookings().subscribe({
      next: res => {
        this.bookings = res;
        this.loading = false;
      },
      error: err => {
        console.error('Instructor bookings error', err);
        this.loading = false;
      }
    });
  }
}
<div class="page-container">

  <h2 class="page-title">Student Bookings</h2>

  <!-- Loading -->
  <p *ngIf="loading" class="text-muted">
    Loading bookings...
  </p>

  <!-- No bookings -->
  <p *ngIf="!loading && bookings.length === 0" class="text-muted">
    No bookings found.
  </p>

  <!-- Bookings Table -->
  <table *ngIf="!loading && bookings.length > 0"
         class="table table-hover table-bordered">
    <thead>
      <tr>
        <th>Student</th>
        <th>Email</th>
        <th>Mobile</th>
        <th>Batch</th>
        <th>Status</th>
        <th>Booking Date</th>
      </tr>
    </thead>

    <tbody>
      <tr *ngFor="let b of bookings">
        <td>{{ b.student.fullName }}</td>
        <td>{{ b.student.email }}</td>
        <td>{{ b.mobileNumber }}</td>
        <td>{{ b.batch.batchName }}</td>
        <td>
          <span
            [ngClass]="{
              'text-success': b.status === 'Booked' || b.status === 'Confirmed',
              'text-warning': b.status === 'Pending',
              'text-danger': b.status === 'Cancelled'
            }">
            {{ b.status }}
          </span>
        </td>
        <td>{{ b.bookingDate | date:'mediumDate' }}</td>
      </tr>
    </tbody>
  </table>

</div>
