import { Component, HostListener, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { API } from 'src/app/core/api/api-constants.service';
import { BookingUserService } from 'src/app/services/bookings/booking-user.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css'],
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  selectedService: any = null;
  batches: any[] = [];

  loading = true;
  isMobile = false;

  activeBatchId: string | null = null;
  studentMobile = '';
  processing = false;

  constructor(
    private http: HttpClient,
    private router: Router,
    private bookingApi: BookingUserService
  ) {}

  ngOnInit(): void {
    this.loadServices();
    this.checkScreen();
  }

  @HostListener('window:resize')
  checkScreen() {
    this.isMobile = window.innerWidth <= 768;
  }

  // ðŸ”¹ Load services
  loadServices(): void {
    this.http.get<any[]>(API.services).subscribe({
      next: (res) => {
        this.services = res;
        this.loading = false;
      },
      error: () => {
        alert('Failed to load services');
        this.loading = false;
      }
    });
  }

  // ðŸ”¹ Select service
  selectService(service: any): void {
    this.selectedService = service;
    this.activeBatchId = null;
    this.studentMobile = '';
    this.loadBatches(service.serviceId);
  }

  // ðŸ”¹ Load batches
  loadBatches(serviceId: string): void {
    this.http
      .get<any[]>(`${API.batches}/service/${serviceId}`)
      .subscribe(res => this.batches = res);
  }

  // ðŸ”¹ Show mobile input for selected batch
  openMobileInput(batchId: string): void {
    this.activeBatchId = batchId;
    this.studentMobile = '';
  }

  // ðŸ”¹ Confirm booking
  confirmBooking(batchId: string): void {
    if (this.processing) return;

    const userId = localStorage.getItem('userId');
    if (!userId) {
      alert('Please login to continue');
      return;
    }

    if (!this.studentMobile || this.studentMobile.length !== 10) {
      alert('Please enter a valid 10-digit mobile number');
      return;
    }

    this.processing = true;

    const payload = {
      userId: userId,
      batchId: batchId,
      studentMobile: this.studentMobile   
    };

    this.bookingApi.createBooking(payload).subscribe({
      next: (res: any) => {
        this.processing = false;
        this.activeBatchId = null;
        this.studentMobile = '';


        this.router.navigate(['/user/payment', res.bookingId]);
      },
      error: (err) => {
        this.processing = false;
        alert(err?.error || 'Booking failed');
      }
    });
  }
}
<div class="book-batch-page">
  <h2 class="page-title">Book a Batch</h2>

  <div class="services-grid" *ngIf="!loading">
    <div
      class="service-card"
      *ngFor="let service of services"
      (click)="selectService(service)"
      [class.active]="service === selectedService"
    >

      <!-- SERVICE HEADER -->
      <h4 class="service-title">{{ service.name }}</h4>

      <!-- DESCRIPTION -->
      <p
        class="service-desc"
        [class.expanded]="service.expanded"
      >
        {{ service.description }}
      </p>

      <!-- SHOW MORE -->
      <button
        *ngIf="service.description?.length > 140"
        class="show-more-btn"
        (click)="toggleDescription(service); $event.stopPropagation()"
      >
        {{ service.expanded ? 'Show less' : 'Show more' }}
      </button>

      <!-- INSTRUCTOR PREVIEW -->
      <div class="instructor-preview">
        <img
          src="assets/Images/default-avatar.png"
          class="instructor-photo"
        />

        <div class="instructor-info">
          <strong>Instructor</strong>
          <p class="masked-mobile">ðŸ“ž 70******67</p>
        </div>
      </div>

      <span class="view-batches">
        View Available Batches â†’
      </span>

      <!-- MOBILE BATCHES (UNCHANGED LOGIC) -->
      <div
        class="mobile-batches"
        *ngIf="service === selectedService && isMobile && batches.length"
      >
        <div class="batch-card" *ngFor="let batch of batches">

          <div class="batch-info">
            <strong>{{ batch.batchName }}</strong>
            <p>Start Date: {{ batch.startDate | date }}</p>
            <p class="batch-price">â‚¹{{ batch.price }}</p>
            <p class="batch-seats">Seats: {{ batch.availableSeats }}</p>
          </div>

          <button
            class="btn-primary"
            *ngIf="activeBatchId !== batch.batchId"
            [disabled]="batch.availableSeats === 0"
            (click)="openMobileInput(batch.batchId); $event.stopPropagation()"
          >
            Book Now
          </button>

          <div *ngIf="activeBatchId === batch.batchId" class="mobile-input-box">
            <input
              type="tel"
              placeholder="Enter mobile number"
              [(ngModel)]="studentMobile"
              maxlength="10"
            />
            <button
              class="btn-primary"
              [disabled]="processing"
              (click)="confirmBooking(batch.batchId)"
            >
              {{ processing ? 'Processing...' : 'Confirm' }}
