import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BatchManageService } from 'src/app/services/batches/batch-manage.service';
import { ServiceQueryService } from 'src/app/services/services/service-query.service';

@Component({
  selector: 'app-create-batch',
  templateUrl: './create-batch.component.html',
  styleUrls: ['./create-batch.component.css'],
})
export class CreateBatchComponent implements OnInit {

  // ===============================
  // UI MODE (important for shared UI)
  // ===============================
  batchId = null; // ‚úÖ CREATE MODE

  // ===============================
  // Form Fields
  // ===============================
  batchName = '';
  description = '';
  price = 0;
  startDate = '';
  serviceId = '';
  totalSeats = 0;
  mobileNumber = '';
  minStartDate = '';

  // ===============================
  // UI State
  // ===============================
  services: any[] = [];
  isSubmitting = false;
  showSuccessToast = false;

  constructor(
    private batchManageService: BatchManageService,
    private serviceQueryService: ServiceQueryService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.setMinStartDate();
    this.fetchServices();
  }

  // ===============================
  // Helpers
  // ===============================
  setMinStartDate(): void {
    const today = new Date();
    this.minStartDate = today.toISOString().split('T')[0];
  }

  fetchServices(): void {
    this.serviceQueryService.getAllServices().subscribe({
      next: (services) => {
        this.services = services;
      },
      error: (error) => {
        console.error('Error loading services', error);
      },
    });
  }

  // ===============================
  // CREATE BATCH
  // ===============================
  submitBatch(): void {
    if (this.isSubmitting) return;

    const payload = {
      batchId: this.batchId,
      batchName: this.batchName,
      description: this.description,
      price: this.price,

      totalSeats: this.totalSeats,
      availableSeats: this.totalSeats, 

      startDate: this.startDate,
      serviceId: this.serviceId,
      mobileNumber: this.mobileNumber,
    };

    this.isSubmitting = true;

    this.batchManageService.createBatch(payload).subscribe({
      next: () => {
        this.showSuccessToast = true;

        setTimeout(() => {
          this.showSuccessToast = false;
          this.router.navigate(['/instructor/batches']);
        }, 1800);
      },
      error: (error) => {
        console.error('Batch creation failed', error);
        this.isSubmitting = false;
      },
    });
  }
}
<div class="batch-container">
  <h2 class="page-title">Create New Batch</h2>

  <form class="batch-form" (ngSubmit)="submitBatch()" #batchForm="ngForm">
    <!-- Batch Name -->
    <div class="form-group">
      <label>Batch Name</label>
      <input
        type="text"
        class="form-control"
        name="batchName"
        [(ngModel)]="batchName"
        required
        placeholder="Enter batch name"
      />
    </div>

    <!-- Description -->
    <div class="form-group">
      <label>Description</label>
      <textarea
        class="form-control"
        rows="3"
        name="description"
        [(ngModel)]="description"
        required
        placeholder="Brief description of the batch"
      ></textarea>
    </div>

    <!-- Price -->
    <!-- Price & Seats (2-column on desktop) -->
<div class="form-row">

  <div class="form-group">
    <label>Price (‚Çπ)</label>
    <div class="input-icon">
      <span class="icon">‚Çπ</span>
      <input
        type="number"
        class="form-control"
        name="price"
        [(ngModel)]="price"
        required
        min="0"
      />
    </div>
  </div>

  <div class="form-group">
    <label>Total Seats</label>
    <div class="input-icon">
      <span class="icon">üéüÔ∏è</span>
      <input
        type="number"
        class="form-control"
        name="totalSeats"
        [(ngModel)]="totalSeats"
        required
        min="1"
      />
    </div>
  </div>

</div>


    <!-- Start Date -->
    <div class="form-group">
  <label>Start Date</label>
  <div class="input-icon">
    <span class="icon">üìÖ</span>
    <input
      type="date"
      class="form-control"
      name="startDate"
      [(ngModel)]="startDate"
      [min]="minStartDate"
      required
    />
  </div>
</div>


    <!-- Service -->
    <div class="form-group">
      <label>Service</label>
      <select
        class="form-control"
        name="serviceId"
        [(ngModel)]="serviceId"
        required
      >
        <option value="">-- Select Service --</option>
        <option *ngFor="let service of services" [value]="service.serviceId">
          {{ service.name }}
        </option>
      </select>
    </div>

    <!-- Contact Mobile Number -->
   <div class="form-group">
  <label>Contact Mobile Number</label>
  <div class="input-icon">
    <span class="icon">üìû</span>
    <input
      type="tel"
      class="form-control"
      name="mobileNumber"
      [(ngModel)]="mobileNumber"
      required
      pattern="[0-9]{10}"
      placeholder="Enter 10-digit mobile number"
    />
  </div>
</div>


    <!-- Submit -->
    <button
      class="btn btn-primary-custom"
      type="submit"
      [disabled]="batchForm.invalid"
    >
      Create Batch
    </button>
  </form>
</div>
