import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ServiceManageService } from 'src/app/services/services/service-manage.service';
import { CreateServiceRequest } from 'src/app/interfaces/create-service-request';

@Component({
  selector: 'app-create-service',
  templateUrl: './create-service.component.html',
  styleUrls: ['./create-service.component.css']
})
export class CreateServiceComponent implements OnInit {

  serviceId: string | null = null;
  isEditMode = false;
  loading = false;

  model: CreateServiceRequest = {
    name: '',
    description: '',
    price: 0
  };

  // ✅ POPUP STATES (ADDED)
  showSuccessPopup = false;
  showErrorPopup = false;
  errorMessage = 'Something went wrong. Please try again.';

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private serviceApi: ServiceManageService
  ) {}

  ngOnInit(): void {
    this.serviceId = this.route.snapshot.paramMap.get('id');
    this.isEditMode = !!this.serviceId;
  }

  submit() {
    if (!this.model.name || this.model.price <= 0) {
      return;
    }

    this.loading = true;

    if (this.isEditMode && this.serviceId) {
      this.serviceApi.updateService(this.serviceId, this.model)
        .subscribe({
          next: () => {
            this.loading = false;
            this.showSuccessPopup = true;

            setTimeout(() => {
              this.showSuccessPopup = false;
              this.router.navigate(['/instructor/services']);
            }, 1800);
          },
          error: (err) => {
            this.loading = false;
            this.showErrorPopup = true;
            this.errorMessage = err?.error || 'Failed to update service.';
          }
        });
    } else {
      this.serviceApi.createService(this.model)
        .subscribe({
          next: () => {
            this.loading = false;
            this.showSuccessPopup = true;

            setTimeout(() => {
              this.showSuccessPopup = false;
              this.router.navigate(['/instructor/dashboard']);
            }, 1800);
          },
          error: (err) => {
            this.loading = false;
            this.showErrorPopup = true;
            this.errorMessage = err?.error || 'Failed to create service.';
          }
        });
    }
  }

  cancel() {
    this.router.navigate(['/instructor/services']);
  }

  // ✅ CLOSE ERROR POPUP
  closeErrorPopup(): void {
    this.showErrorPopup = false;
  }
}
<div class="service-form-container">

  <h2 class="title">
    {{ isEditMode ? 'Edit Service' : 'Create New Service' }}
  </h2>

  <form (ngSubmit)="submit()" #form="ngForm">

    <!-- Service Name -->
    <div class="form-group">
      <label>Service Name *</label>
      <input
        type="text"
        class="form-control"
        name="name"
        [(ngModel)]="model.name"
        required
        placeholder="Enter service name"
      />
    </div>

    <!-- Description -->
    <div class="form-group">
      <label>Description</label>
      <textarea
        class="form-control"
        rows="4"
        name="description"
        [(ngModel)]="model.description"
        placeholder="Describe the service"
      ></textarea>
    </div>

    <!-- Price -->
    <div class="form-group">
      <label>Price (₹) *</label>
      <input
        type="number"
        class="form-control"
        name="price"
        [(ngModel)]="model.price"
        required
        min="1"
      />
    </div>

    <!-- Buttons -->
    <div class="actions">
      <button
        type="submit"
        class="btn btn-primary"
        [disabled]="loading || form.invalid"
      >
        {{ loading ? 'Saving...' : (isEditMode ? 'Update Service' : 'Create Service') }}
      </button>

      <button
        type="button"
        class="btn btn-secondary"
        (click)="cancel()"
      >
        Cancel
      </button>
    </div>

  </form>
</div>

<!-- ✅ SUCCESS POPUP -->
<div class="popup-overlay" *ngIf="showSuccessPopup">
  <div class="popup success">
    <h3>✅ Success</h3>
    <p>
      {{ isEditMode ? 'Service updated successfully.' : 'Service created successfully.' }}
    </p>
  </div>
</div>

<!-- ❌ ERROR POPUP -->
<div class="popup-overlay" *ngIf="showErrorPopup">
  <div class="popup error">
    <h3>❌ Error</h3>
    <p>{{ errorMessage }}</p>
    <button class="btn btn-outline" (click)="closeErrorPopup()">
      Close
    </button>
  </div>
</div>
