// src/app/services/batches/batch-manage.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { API } from 'src/app/core/api/api-constants.service';

@Injectable({
  providedIn: 'root'
})
export class BatchManageService {

  constructor(private http: HttpClient) {}

  /**
   * Create a new batch (Instructor/Admin)
   */
  createBatch(payload: CreateBatchRequest): Observable<any> {
    return this.http.post(`${API.batches}/manage`, payload);
  }

  /**
   * Update an existing batch
   */
  updateBatch(batchId: string, payload: UpdateBatchRequest): Observable<any> {
    return this.http.put(`${API.batches}/manage/${batchId}`, payload);
  }

  /**
   * Delete a batch
   */
  deleteBatch(batchId: string): Observable<any> {
    return this.http.delete(`${API.batches}/manage/${batchId}`);
  }
}

/* ===============================
   Request Models (recommended)
================================ */

export interface CreateBatchRequest {
  batchName: string;
  description: string;
  price: number;
  startDate: string;
  serviceId: string;
}

export interface UpdateBatchRequest extends CreateBatchRequest {}
